public class Competition {
    private String competitionName;
    private String location;
    private int numTeams;
    private String date;
    private boolean isActive;

    public Competition(String name, String location, int numTeams, String date) {
        this.competitionName = name;
        this.location = location;
        this.numTeams = numTeams;
        this.date = date;
        this.isActive = false;
    }

    // Getters
    public String getName() { return competitionName; }
    public String getLocation() { return location; }
    public int getNumTeams() { return numTeams; }
    public String getDate() { return date; }
    public boolean isActive() { return isActive; }

    // Setters
    public void setActive(boolean active) { this.isActive = active; }

    public String toString() {
        return competitionName + " in " + location + " (" + date + ") - " + 
               numTeams + " teams, " + (isActive ? "Active" : "Scheduled");
    }
}

