public class CompetitionFilter {

    // Filter by minimum number of teams
    public static void filterByTeamCount(Competition[] competitions, int minTeams) {
        System.out.println("\nüèÜ Competitions with " + minTeams + "+ teams:");
        boolean found = false;

        for (Competition comp : competitions) {
            if (comp.getNumTeams() >= minTeams) {
                System.out.println("- " + comp);
                found = true;
            }
        }

        if (!found) {
            System.out.println("No competitions found with " + minTeams + "+ teams.");
        }
    }

    // Filter by location
    public static void filterByLocation(Competition[] competitions, String location) {
        System.out.println("\nüìç Competitions in " + location + ":");
        boolean found = false;

        for (Competition comp : competitions) {
            if (comp.getLocation().toLowerCase().contains(location.toLowerCase())) {
                System.out.println("- " + comp);
                found = true;
            }
        }

        if (!found) {
            System.out.println("No competitions found in " + location + ".");
        }
    }

    // Usage example
    public static void main(String[] args) {
        Competition[] season = {
            new Competition("Local Qualifier", "Tech High School", 24, "2024-01-15"),
            new Competition("Regional Championship", "University Arena", 48, "2024-02-12"),
            new Competition("State Finals", "Convention Center", 64, "2024-03-05")
        };

        // Apply various filters
        filterByTeamCount(season, 50);
        filterByLocation(season, "Arena");
    }
}
