// Declaration and initialization
int[] robotScores = new int[5];
String[] teamNames = {"Alpha", "Beta", "Gamma"};
double[] sensorReadings = new double[10];

// Accessing elements
robotScores[0] = 95;  // Set first element
robotScores[1] = 87;  // Set second element

// Getting array length
int numberOfTeams = teamNames.length;
System.out.println("Number of teams: " + numberOfTeams);

int[] motorPowers = {75, 50, 100, 25, 90};

// Traditional for loop
for (int i = 0; i < motorPowers.length; i++) {
    System.out.println("Motor " + i + " power: " + motorPowers[i]);
}

// Enhanced for loop (for-each)
for (int power : motorPowers) {
    System.out.println("Power level: " + power);
}

// Finding maximum value
int maxPower = motorPowers[0];
for (int power : motorPowers) {
    if (power > maxPower) {
        maxPower = power;
    }
}
System.out.println("Maximum power: " + maxPower);

