// Method 1: Declare and initialize separately
double[] batteryLevels = new double[5];

// Method 2: Declare and initialize with values
double[] batteryLevels = {85.5, 67.2, 92.1, 15.8, 78.9};

// Method 3: Declare, then assign values
double[] batteryLevels = new double[5];
batteryLevels[0] = 85.5;
batteryLevels[1] = 67.2;
batteryLevels[2] = 92.1;
batteryLevels[3] = 15.8;
batteryLevels[4] = 78.9;

public class BatteryManager {
    public static void main(String[] args) {
        double[] batteryLevels = new double[5];
        String[] robotNames = {"Alpha", "Beta", "Gamma", "Delta", "Echo"};

        // Fill array with different values
        batteryLevels[0] = 85.5;  // Alpha robot
        batteryLevels[1] = 67.2;  // Beta robot
        batteryLevels[2] = 92.1;  // Gamma robot
        batteryLevels[3] = 15.8;  // Delta robot (low!)
        batteryLevels[4] = 78.9;  // Echo robot

        // Display all battery levels
        System.out.println("=== Robot Battery Status ===");
        for (int i = 0; i < batteryLevels.length; i++) {
            System.out.println(robotNames[i] + ": " + batteryLevels[i] + "%");
        }
    }
}

public static double calculateAverageBattery(double[] batteryLevels) {
    double sum = 0;

    // Add up all battery levels
    for (int i = 0; i < batteryLevels.length; i++) {
        sum += batteryLevels[i];
    }

    // Calculate average
    double average = sum / batteryLevels.length;

    System.out.println("Total battery: " + sum + "%");
    System.out.println("Average battery: " + average + "%");

    return average;
}

// Alternative using enhanced for loop
public static double calculateAverageBatteryEnhanced(double[] batteryLevels) {
    double sum = 0;

    for (double level : batteryLevels) {
        sum += level;
    }

    return sum / batteryLevels.length;
}

public static void findLowBatteryRobots(String[] robotNames, double[] batteryLevels) {
