// Enhanced version that returns array of low battery robots
public static String[] getLowBatteryRobotNames(String[] robotNames, double[] batteryLevels) {
    // First, count how many robots have low battery
    int lowBatteryCount = 0;
    for (double level : batteryLevels) {
        if (level < 20.0) {
            lowBatteryCount++;
        }
    }

    // Create array for low battery robot names
    String[] lowBatteryRobots = new String[lowBatteryCount];
    int index = 0;

    for (int i = 0; i < batteryLevels.length; i++) {
        if (batteryLevels[i] < 20.0) {
            lowBatteryRobots[index] = robotNames[i];
            index++;
        }
    }

    return lowBatteryRobots;
}
