package frc.robot.subsystems;

import edu.wpi.first.wpilibj.Encoder;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

public class Drivetrain {
    // Motor controllers
    private final CANSparkMax m_leftMotor = new CANSparkMax(1, MotorType.kBrushless);
    private final CANSparkMax m_rightMotor = new CANSparkMax(2, MotorType.kBrushless);

    // Encoders
    private final Encoder m_leftEncoder = new Encoder(0, 1); // DIO ports 0 and 1
    private final Encoder m_rightEncoder = new Encoder(2, 3); // DIO ports 2 and 3

    // Encoder configuration
    private static final double WHEEL_DIAMETER_INCHES = 6.0;
    private static final double GEAR_RATIO = 10.71; // Example gear ratio
    private static final double ENCODER_PULSES_PER_REVOLUTION = 4096; // For quadrature encoder

    public Drivetrain() {
        // Configure encoders
        m_leftEncoder.setDistancePerPulse(
            (Math.PI * WHEEL_DIAMETER_INCHES) / (GEAR_RATIO * ENCODER_PULSES_PER_REVOLUTION)
        );
        m_rightEncoder.setDistancePerPulse(
            (Math.PI * WHEEL_DIAMETER_INCHES) / (GEAR_RATIO * ENCODER_PULSES_PER_REVOLUTION)
        );

        // Set encoder direction (may need to be reversed)
        m_leftEncoder.setReverseDirection(false);
        m_rightEncoder.setReverseDirection(true);

        // Reset encoders to zero
        resetEncoders();

        SmartDashboard.putString("Drivetrain Status", "Encoders Initialized");
    }

    public void resetEncoders() {
        m_leftEncoder.reset();
        m_rightEncoder.reset();
    }

    public double getLeftDistance() {
        return m_leftEncoder.getDistance();
    }

    public double getRightDistance() {
        return m_rightEncoder.getDistance();
    }

    public double getAverageDistance() {
        return (getLeftDistance() + getRightDistance()) / 2.0;
    }

    public double getLeftSpeed() {
        return m_leftEncoder.getRate();
    }

    public double getRightSpeed() {
        return m_rightEncoder.getRate();
    }

    public double getAverageSpeed() {
        return (getLeftSpeed() + getRightSpeed()) / 2.0;
    }

    public void updateDashboard() {
        SmartDashboard.putNumber("Left Distance (inches)", getLeftDistance());
        SmartDashboard.putNumber("Right Distance (inches)", getRightDistance());
        SmartDashboard.putNumber("Average Distance (inches)", getAverageDistance());
        SmartDashboard.putNumber("Left Speed (in/s)", getLeftSpeed());
        SmartDashboard.putNumber("Right Speed (in/s)", getRightSpeed());
        SmartDashboard.putNumber("Average Speed (in/s)", getAverageSpeed());
    }
}

package frc.robot.subsystems;

import edu.wpi.first.wpilibj.ADXRS450_Gyro;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

public class Drivetrain {
    // ... existing motor and encoder declarations ...

    // Gyroscope
    private final ADXRS450_Gyro m_gyro = new ADXRS450_Gyro();

    public Drivetrain() {
        // ... existing initialization ...

        // Calibrate gyro (this takes a few seconds)
        SmartDashboard.putString("Gyro Status", "Calibrating...");
        m_gyro.calibrate();
        SmartDashboard.putString("Gyro Status", "Calibrated");
    }

    public void resetGyro() {
        m_gyro.reset();
    }

    public double getGyroAngle() {
        return m_gyro.getAngle(); // Returns angle in degrees
    }

    public double getGyroRate() {
        return m_gyro.getRate(); // Returns rotation rate in degrees/second
    }

    public double getGyroRadians() {
        return Math.toRadians(getGyroAngle());
    }

    // Helper method to normalize angle to -180 to +180 degrees
    public double normalizeAngle(double angle) {
        while (angle > 180) {
            angle -= 360;
        }
        while (angle < -180) {
            angle += 360;
        }
        return angle;
    }

    // Turn to a specific angle
    public void turnToAngle(double targetAngle) {
        double currentAngle = normalizeAngle(getGyroAngle());
        double error = normalizeAngle(targetAngle - currentAngle);

        // Simple proportional control (we'll cover PID later)
        double turnSpeed = error * 0.02; // Proportional gain

        // Limit turn speed
        turnSpeed = Math.max(-0.5, Math.min(0.5, turnSpeed));

        // Apply minimum speed to overcome friction
        if (Math.abs(turnSpeed) > 0 && Math.abs(turnSpeed) < 0.1) {
            turnSpeed = Math.signum(turnSpeed) * 0.1;
        }

        // Drive the robot
        m_drive.arcadeDrive(0, turnSpeed);
    }

    @Override
    public void updateDashboard() {
        // ... existing encoder dashboard updates ...

        SmartDashboard.putNumber("Gyro Angle (degrees)", getGyroAngle());
        SmartDashboard.putNumber("Gyro Rate (deg/s)", getGyroRate());
        SmartDashboard.putNumber("Gyro Angle (radians)", getGyroRadians());
    }
}

package frc.robot.subsystems;

import edu.wpi.first.wpilibj.DigitalInput;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

public class Arm {
    // Motor controller
    private final CANSparkMax m_armMotor = new CANSparkMax(3, MotorType.kBrushless);

    // Limit switches
    private final DigitalInput m_lowerLimitSwitch = new DigitalInput(4); // DIO port 4
    private final DigitalInput m_upperLimitSwitch = new DigitalInput(5); // DIO port 5

    // Arm state
    private boolean m_atLowerLimit = false;
    private boolean m_atUpperLimit = false;

    public Arm() {
        // Configure motor
        m_armMotor.setInverted(false);
        m_armMotor.setSmartCurrentLimit(30);

        SmartDashboard.putString("Arm Status", "Initialized");
    }

    public void moveUp() {
        // Check upper limit switch before moving up
        if (!m_atUpperLimit) {
            m_armMotor.set(0.5);
        } else {
            m_armMotor.set(0);
        }
    }

    public void moveDown() {
        // Check lower limit switch before moving down
        if (!m_atLowerLimit) {
            m_armMotor.set(-0.5);
        } else {
            m_armMotor.set(0);
        }
    }

    public void stop() {
        m_armMotor.set(0);
    }

    public boolean isAtLowerLimit() {
        return m_lowerLimitSwitch.get(); // Returns true when pressed
    }

    public boolean isAtUpperLimit() {
        return m_upperLimitSwitch.get(); // Returns true when pressed
    }

    public void update() {
        // Update limit switch states
        m_atLowerLimit = isAtLowerLimit();
        m_atUpperLimit = isAtUpperLimit();

        // Stop motor if at limits
        if (m_atLowerLimit && m_armMotor.get() < 0) {
            m_armMotor.set(0);
        }
        if (m_atUpperLimit && m_armMotor.get() > 0) {
            m_armMotor.set(0);
        }
    }

    public void updateDashboard() {
        SmartDashboard.putBoolean("Lower Limit Switch", m_atLowerLimit);
        SmartDashboard.putBoolean("Upper Limit Switch", m_atUpperLimit);
        SmartDashboard.putNumber("Arm Motor Speed", m_armMotor.get());
    }
}

package frc.robot.subsystems;

import edu.wpi.first.wpilibj.AnalogInput;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

public class Arm {
    // ... existing motor and limit switch declarations ...

    // Potentiometer
    private final AnalogInput m_potentiometer = new AnalogInput(0); // Analog port 0

    // Potentiometer configuration
    private static final double POT_VOLTAGE_AT_0_DEGREES = 0.0; // Voltage at 0 degrees
    private static final double POT_VOLTAGE_AT_90_DEGREES = 5.0; // Voltage at 90 degrees
    private static final double POT_ANGLE_OFFSET = 0.0; // Offset in degrees

    public Arm() {
        // ... existing initialization ...

        // Configure potentiometer
        m_potentiometer.setAverageBits(4); // Average 16 samples for stability

        SmartDashboard.putString("Arm Status", "Potentiometer Initialized");
    }

    public double getPotentiometerVoltage() {
        return m_potentiometer.getAverageVoltage();
    }

    public double getArmAngle() {
        double voltage = getPotentiometerVoltage();

        // Convert voltage to angle
        double angle = ((voltage - POT_VOLTAGE_AT_0_DEGREES) / 
                       (POT_VOLTAGE_AT_90_DEGREES - POT_VOLTAGE_AT_0_DEGREES)) * 90.0;

        // Apply offset
        angle += POT_ANGLE_OFFSET;

        return angle;
    }

    public double getArmAngleRadians() {
        return Math.toRadians(getArmAngle());
    }

    // Move arm to specific angle
    public void moveToAngle(double targetAngle) {
        double currentAngle = getArmAngle();
        double error = targetAngle - currentAngle;

        // Simple proportional control
        double speed = error * 0.02; // Proportional gain

        // Limit speed
        speed = Math.max(-0.5, Math.min(0.5, speed));

        // Apply minimum speed
        if (Math.abs(speed) > 0 && Math.abs(speed) < 0.1) {
            speed = Math.signum(speed) * 0.1;
        }

        // Check limit switches
        if (speed > 0 && m_atUpperLimit) {
            speed = 0;
        }
        if (speed < 0 && m_atLowerLimit) {
            speed = 0;
        }

        m_armMotor.set(speed);
    }

    // Check if arm is at target angle (within tolerance)
    public boolean isAtAngle(double targetAngle, double tolerance) {
        return Math.abs(getArmAngle() - targetAngle) < tolerance;
    }

    @Override
    public void updateDashboard() {
        // ... existing dashboard updates ...

        SmartDashboard.putNumber("Potentiometer Voltage", getPotentiometerVoltage());
        SmartDashboard.putNumber("Arm Angle (degrees)", getArmAngle());
        SmartDashboard.putNumber("Arm Angle (radians)", getArmAngleRadians());
    }
}

package frc.robot.subsystems;

import edu.wpi.first.wpilibj.Ultrasonic;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

public class Intake {
    // Motor controller
    private final CANSparkMax m_intakeMotor = new CANSparkMax(4, MotorType.kBrushless);

    // Ultrasonic sensor
    private final Ultrasonic m_ultrasonic = new Ultrasonic(6, 7); // Trigger on DIO 6, Echo on DIO 7

    // Configuration
    private static final double BALL_DETECTION_DISTANCE = 5.0; // inches
    private static final double OBSTACLE_DETECTION_DISTANCE = 12.0; // inches

    public Intake() {
        // Configure motor
        m_intakeMotor.setInverted(false);
        m_intakeMotor.setSmartCurrentLimit(20);

        // Configure ultrasonic sensor
        m_ultrasonic.setAutomaticMode(true); // Enable automatic mode

        SmartDashboard.putString("Intake Status", "Ultrasonic Initialized");
    }

    public double getDistance() {
        return m_ultrasonic.getRangeInches();
    }

    public boolean isBallDetected() {
        return getDistance() < BALL_DETECTION_DISTANCE;
    }

    public boolean isObstacleDetected() {
        return getDistance() < OBSTACLE_DETECTION_DISTANCE;
    }

    public void intake() {
        // Only run intake if no obstacle is detected
        if (!isObstacleDetected()) {
            m_intakeMotor.set(0.7);
        } else {
            m_intakeMotor.set(0);
        }
    }

    public void outtake() {
        m_intakeMotor.set(-0.7);
    }

    public void stop() {
        m_intakeMotor.set(0);
    }

    public void updateDashboard() {
        SmartDashboard.putNumber("Ultrasonic Distance (inches)", getDistance());
        SmartDashboard.putBoolean("Ball Detected", isBallDetected());
        SmartDashboard.putBoolean("Obstacle Detected", isObstacleDetected());
        SmartDashboard.putNumber("Intake Motor Speed", m_intakeMotor.get());
    }
}

public class Robot extends TimedRobot {
    // ... existing subsystem declarations ...

    // Sensor data structure
    public static class SensorData {
        public double leftDistance;
        public double rightDistance;
        public double gyroAngle;
        public double armAngle;
        public boolean ballDetected;
        public boolean atLowerLimit;
        public boolean atUpperLimit;
        public double ultrasonicDistance;
    }

    private SensorData m_sensorData = new SensorData();

    @Override
    public void robotPeriodic() {
        // Update all sensor data
        updateSensorData();

        // Update dashboards
        m_drivetrain.updateDashboard();
        m_arm.updateDashboard();
        m_intake.updateDashboard();

        // Update integrated sensor display
        updateIntegratedDashboard();
    }

    private void updateSensorData() {
        m_sensorData.leftDistance = m_drivetrain.getLeftDistance();
        m_sensorData.rightDistance = m_drivetrain.getRightDistance();
        m_sensorData.gyroAngle = m_drivetrain.getGyroAngle();
        m_sensorData.armAngle = m_arm.getArmAngle();
        m_sensorData.ballDetected = m_intake.isBallDetected();
        m_sensorData.atLowerLimit = m_arm.isAtLowerLimit();
        m_sensorData.atUpperLimit = m_arm.isAtUpperLimit();
        m_sensorData.ultrasonicDistance = m_intake.getDistance();
    }

    private void updateIntegratedDashboard() {
        SmartDashboard.putString("Robot Status", "All Sensors Active");
        SmartDashboard.putNumber("Total Distance Traveled", m_sensorData.leftDistance);
        SmartDashboard.putNumber("Robot Heading", m_sensorData.gyroAngle);
        SmartDashboard.putString("Arm Position", String.format("%.1fÂ°", m_sensorData.armAngle));
        SmartDashboard.putString("Game Element Status", 
            m_sensorData.ballDetected ? "Ball Detected" : "No Ball");
    }

    // Example autonomous behavior using multiple sensors
    private void runSensorBasedAutonomous() {
        double matchTime = DriverStation.getMatchTime();

        if (matchTime > 13.0) {
            // Drive forward while maintaining straight path
            double gyroError = m_sensorData.gyroAngle; // Assuming 0 is straight
            double turnCorrection = gyroError * 0.02;
            m_drivetrain.arcadeDrive(0.5, turnCorrection);

        } else if (matchTime > 11.0) {
            // Turn to face target
            m_drivetrain.turnToAngle(90);

        } else if (matchTime > 9.0) {
            // Move arm to scoring position
            if (!m_sensorData.atUpperLimit) {
                m_arm.moveToAngle(45);
            }

        } else if (matchTime > 7.0) {
            // Intake if ball is detected
            if (m_sensorData.ballDetected) {
                m_intake.intake();
            }

        } else {
            // Stop everything
            m_drivetrain.stop();
            m_arm.stop();
            m_intake.stop();
        }
    }
}

// Example sensor calibration routine:

public void calibrateSensors() {
    SmartDashboard.putString("Calibration Status", "Starting...");

    // Reset encoders
    m_drivetrain.resetEncoders();
    SmartDashboard.putString("Calibration Status", "Encoders Reset");

    // Calibrate gyro
    m_drivetrain.resetGyro();
    SmartDashboard.putString("Calibration Status", "Gyro Reset");

    // Test limit switches
    boolean lowerLimitWorking = m_arm.isAtLowerLimit();
    boolean upperLimitWorking = m_arm.isAtUpperLimit();

    SmartDashboard.putBoolean("Lower Limit Working", lowerLimitWorking);
    SmartDashboard.putBoolean("Upper Limit Working", upperLimitWorking);

    // Test potentiometer
    double potValue = m_arm.getArmAngle();
    SmartDashboard.putNumber("Potentiometer Reading", potValue);

    // Test ultrasonic
    double ultrasonicValue = m_intake.getDistance();
    SmartDashboard.putNumber("Ultrasonic Reading", ultrasonicValue);

    SmartDashboard.putString("Calibration Status", "Complete");
}

// Call this in robotInit() or testInit()
@Override
public void testInit() {
    calibrateSensors();
}
