import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

public class ExampleSubsystem {
    private double motorSpeed = 0.5;
    private String status = "Running";
    private boolean isEnabled = true;

    public void updateDashboard() {
        // SmartDashboard methods work with Elastic through NetworkTables
        SmartDashboard.putNumber("Motor Speed", motorSpeed);
        SmartDashboard.putString("Robot Status", status);
        SmartDashboard.putBoolean("System Enabled", isEnabled);

        // Multiple values
        SmartDashboard.putNumber("Left Motor", 0.75);
        SmartDashboard.putNumber("Right Motor", 0.75);
        SmartDashboard.putNumber("Arm Position", 45.0);
    }
}

import edu.wpi.first.networktables.NetworkTableInstance;
import edu.wpi.first.networktables.NetworkTable;

public class ExampleSubsystem {
    private NetworkTable m_table;
    private double targetSpeed;
    private boolean enableSystem;

    public ExampleSubsystem() {
        m_table = NetworkTableInstance.getDefault().getTable("SmartDashboard");
    }

    public void readDashboard() {
        targetSpeed = m_table.getEntry("Target Speed").getDouble(0.5);

        enableSystem = m_table.getEntry("Enable System").getBoolean(false);

        String mode = m_table.getEntry("Robot Mode").getString("Teleop");

        if (enableSystem) {
            setMotorSpeed(targetSpeed);
        }
    }
}

package frc.robot.subsystems;

import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;
import edu.wpi.first.wpilibj2.command.SubsystemBase;
import com.revrobotics.spark.SparkMax;
import com.revrobotics.spark.SparkLowLevel.MotorType;

public class Arm extends SubsystemBase {
    private final SparkMax m_armMotor = new SparkMax(3, MotorType.kBrushless);
    private double m_targetPosition = 0.0;
    private boolean m_pidEnabled = false;

    public Arm() {
        SmartDashboard.putNumber("Arm Target Position", 0.0);
        SmartDashboard.putBoolean("Arm PID Enabled", false);
    }

    @Override
    public void periodic() {
        m_targetPosition = SmartDashboard.getNumber("Arm Target Position", 0.0);
        m_pidEnabled = SmartDashboard.getBoolean("Arm PID Enabled", false);

        SmartDashboard.putNumber("Arm Current Position", m_armMotor.getEncoder().getPosition());
        SmartDashboard.putNumber("Arm Current Speed", m_armMotor.getEncoder().getVelocity());
        SmartDashboard.putNumber("Arm Current", m_armMotor.getOutputCurrent());
        SmartDashboard.putBoolean("Arm At Target", isAtPosition(m_targetPosition, 2.0));

        // Control arm based on dashboard values
        if (m_pidEnabled) {
            // (PID implementation would go here)
        }
    }

    private boolean isAtPosition(double target, double tolerance) {
        double current = m_armMotor.getEncoder().getPosition();
        return Math.abs(current - target) < tolerance;
    }
}

import edu.wpi.first.math.geometry.Pose2d;
import edu.wpi.first.math.geometry.Rotation2d;
import edu.wpi.first.wpilibj.smartdashboard.Field2d;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;
import edu.wpi.first.wpilibj2.command.SubsystemBase;

public class Drivetrain extends SubsystemBase {
    private final Field2d m_field = new Field2d();
    private Pose2d m_robotPose;

    public Drivetrain() {
        // Field2d automatically publishes to NetworkTables
        // Elastic will automatically display it
        SmartDashboard.putData("Field", m_field);
    }

    @Override
    public void periodic() {
        m_robotPose = getOdometryPose();

        m_field.setRobotPose(m_robotPose);

        // You can also add trajectory visualizations
        // m_field.getObject("trajectory").setTrajectory(trajectory);
    }
}
