package frc.robot;

import edu.wpi.first.wpilibj.TimedRobot;
import edu.wpi.first.wpilibj2.command.Command;
import edu.wpi.first.wpilibj2.command.CommandScheduler;

public class Robot extends TimedRobot {
    private Command m_autonomousCommand;

    @Override
    public void robotInit() {
        // This method is called once when the robot starts
        // Initialize hardware, sensors, and subsystems here
    }

    @Override
    public void robotPeriodic() {
        // This method is called repeatedly while the robot is on
        // Update dashboard, run periodic tasks
        CommandScheduler.getInstance().run();
    }

    @Override
    public void autonomousInit() {
        // Called once when autonomous mode starts
        // Schedule autonomous commands here
    }

    @Override
    public void autonomousPeriodic() {
        // Called repeatedly during autonomous
        // Autonomous logic goes here
    }

    @Override
    public void teleopInit() {
        // Called once when teleop mode starts
        // Cancel autonomous commands, initialize teleop
    }

    @Override
    public void teleopPeriodic() {
        // Called repeatedly during teleop
        // Teleop control logic goes here
    }

    @Override
    public void testInit() {
        // Called once when test mode starts
    }

    @Override
    public void testPeriodic() {
        // Called repeatedly during test mode
        // Test code goes here
    }
}

plugins {
    id "java"
    id "edu.wpi.first.GradleRIO" version "2024.1.1"  // WPILib version
}

repositories {
    mavenCentral()  // Standard Java libraries
    maven { url = "https://maven.phoenixframework.org/" }  // CTRE
    maven { url = "https://www.revrobotics.com/content/sw/max/sdk/maven/" }  // REV
}

dependencies {
    // WPILib dependencies (automatically included)
    implementation wpi.java.deps.wpilib()
    implementation wpi.java.vendor.java()

    // Example: Adding CTRE Phoenix library
    // implementation "com.ctre.phoenix:api-java:5.30.3"

    // Example: Adding REV Robotics library
    // implementation "com.revrobotics.frc:SparkMax-java:1.11.1"
}

// WPILib version configuration
wpi {
    javaVersion = JavaVersion.VERSION_17
}
