package frc.robot;

import edu.wpi.first.wpilibj.DataLogManager;
import edu.wpi.first.wpilibj.DriverStation;
import edu.wpi.first.wpilibj.TimedRobot;
import edu.wpi.first.wpilibj2.command.CommandScheduler;

public class Robot extends TimedRobot {
    @Override
    public void robotInit() {
        // Logs are saved to /home/lvuser/logs on the roboRIO
        DataLogManager.start();

        // Log match information
        DataLogManager.log("Robot initialized");

        // Log driver station data (match info, alliance, etc.)
        DriverStation.startDataLog(DataLogManager.getLog());
    }

    @Override
    public void robotPeriodic() {
        CommandScheduler.getInstance().run();
    }
}

package frc.robot.subsystems;

import edu.wpi.first.wpilibj.DataLogManager;
import edu.wpi.first.wpilibj2.command.SubsystemBase;

public class Drivetrain extends SubsystemBase {
    private double m_leftEncoderPosition = 0.0;
    private double m_rightEncoderPosition = 0.0;

    @Override
    public void periodic() {
        // Log encoder positions (double values)
        DataLogManager.log("Drivetrain/LeftEncoder", m_leftEncoderPosition);
        DataLogManager.log("Drivetrain/RightEncoder", m_rightEncoderPosition);

        // Log calculated values
        double averagePosition = (m_leftEncoderPosition + m_rightEncoderPosition) / 2.0;
        DataLogManager.log("Drivetrain/AveragePosition", averagePosition);

        // Log boolean values
        boolean isMoving = Math.abs(averagePosition) > 0.01;
        DataLogManager.log("Drivetrain/IsMoving", isMoving);
    }
}

