package frc.robot.subsystems;

import org.littletonrobotics.junction.Logger;
import edu.wpi.first.wpilibj2.command.SubsystemBase;

public class Drivetrain extends SubsystemBase {
    // AdvantageKit automatically logs many values through IO layer
    // You can also log custom data:

    private double m_leftPosition = 0.0;
    private double m_rightPosition = 0.0;
    private double m_targetVelocity = 0.0;

    @Override
    public void periodic() {
        // Log custom values (doubles)
        Logger.recordOutput("Drivetrain/LeftPosition", m_leftPosition);
        Logger.recordOutput("Drivetrain/RightPosition", m_rightPosition);
        Logger.recordOutput("Drivetrain/TargetVelocity", m_targetVelocity);

        // Log calculated values
        double averagePosition = (m_leftPosition + m_rightPosition) / 2.0;
        Logger.recordOutput("Drivetrain/AveragePosition", averagePosition);

        // Log booleans
        Logger.recordOutput("Drivetrain/IsMoving", Math.abs(m_targetVelocity) > 0.01);

        // Log strings
        String state = m_targetVelocity > 0 ? "Forward" : "Stopped";
        Logger.recordOutput("Drivetrain/State", state);

        // Log arrays
        double[] wheelSpeeds = {m_leftPosition, m_rightPosition};
        Logger.recordOutput("Drivetrain/WheelSpeeds", wheelSpeeds);

        // AdvantageKit supports struct logging for structured data
        // AdvantageScope provides first-class support for structs
    }
}

