import swervelib.SwerveDrive;
import swervelib.parser.SwerveParser;
import edu.wpi.first.math.util.Units;
import java.io.File;
import edu.wpi.first.wpilibj.Filesystem;

public class Drivetrain extends SubsystemBase {
    private final SwerveDrive swerveDrive;

    public Drivetrain() {
        try {
            // Load configuration from deploy/swerve directory
            double maximumSpeed = Units.feetToMeters(14.5);
            File swerveJsonDirectory = new File(Filesystem.getDeployDirectory(), "swerve");

            // Create SwerveDrive from configuration
            this.swerveDrive = new SwerveParser(swerveJsonDirectory).createSwerveDrive(maximumSpeed);

        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    public void drive(Translation2d translation, double rotation, boolean fieldRelative) {
        swerveDrive.drive(translation, rotation, fieldRelative, false);
    }

    public Pose2d getPose() {
        return swerveDrive.getPose();
    }
}
