public class WellOrganizedOpMode extends OpMode {
    // Hardware components - grouped by function
    private DcMotor leftDrive, rightDrive;
    private Servo armServo, clawServo;
    private ColorSensor colorSensor;
    private DistanceSensor distanceSensor;

    // State variables - clearly named and grouped
    private RobotState currentState = RobotState.IDLE;
    private double targetArmPosition = 0.5;
    private boolean isClawOpen = false;

    // Configuration constants
    private static final double ARM_SPEED = 0.02;
    private static final double CLAW_OPEN_POSITION = 1.0;
    private static final double CLAW_CLOSED_POSITION = 0.0;

    @Override
    public void init() {
        initializeHardware();
        configureHardware();
        resetRobotState();
        displayInitializationStatus();
    }

    @Override
    public void loop() {
        processDriverInput();
        updateRobotState();
        controlHardware();
        updateTelemetry();
    }

    // Hardware initialization - focused and clear
    private void initializeHardware() {
        try {
            // Initialize drive motors
            leftDrive = hardwareMap.get(DcMotor.class, "left_drive");
            rightDrive = hardwareMap.get(DcMotor.class, "right_drive");

            // Initialize servos
            armServo = hardwareMap.get(Servo.class, "arm_servo");
            clawServo = hardwareMap.get(Servo.class, "claw_servo");

            // Initialize sensors
            colorSensor = hardwareMap.get(ColorSensor.class, "color_sensor");
            distanceSensor = hardwareMap.get(DistanceSensor.class, "distance_sensor");

        } catch (Exception e) {
            telemetry.addData("ERROR", "Hardware initialization failed: " + e.getMessage());
        }
    }

    // Hardware configuration - separate from initialization
    private void configureHardware() {
        if (leftDrive != null && rightDrive != null) {
            leftDrive.setDirection(DcMotor.Direction.FORWARD);
            rightDrive.setDirection(DcMotor.Direction.REVERSE);
            leftDrive.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
            rightDrive.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        }
    }

    // State management - clear and focused
    private void resetRobotState() {
        currentState = RobotState.IDLE;
        targetArmPosition = 0.5;
        isClawOpen = false;
    }

    // Input processing - handle one concern at a time
    private void processDriverInput() {
        processDriveInput();
        processArmInput();
        processClawInput();
        processAutonomousTriggers();
    }

    private void processDriveInput() {
        double forward = -gamepad1.left_stick_y;
        double turn = gamepad1.right_stick_x;

        double leftPower = forward + turn;
        double rightPower = forward - turn;

        // Apply deadzone
        if (Math.abs(leftPower) < 0.1) leftPower = 0.0;
        if (Math.abs(rightPower) < 0.1) rightPower = 0.0;

        setDrivePowers(leftPower, rightPower);
    }

    private void processArmInput() {
        if (gamepad1.dpad_up) {
            targetArmPosition = Math.min(1.0, targetArmPosition + ARM_SPEED);
        } else if (gamepad1.dpad_down) {
            targetArmPosition = Math.max(0.0, targetArmPosition - ARM_SPEED);
        }
    }

    private void processClawInput() {
        if (gamepad1.a && !isClawOpen) {
            openClaw();
        } else if (gamepad1.b && isClawOpen) {
            closeClaw();
        }
    }

    private void processAutonomousTriggers() {
        if (gamepad1.x) {
            startAutonomousSequence();
        } else if (gamepad1.y) {
            stopAutonomousSequence();
        }
    }

    // State updates - separate from input processing
    private void updateRobotState() {
        updateArmPosition();
        updateAutonomousState();
        checkSafetyConditions();
    }

    private void updateArmPosition() {
        if (armServo != null) {
            armServo.setPosition(targetArmPosition);
        }
    }

    private void updateAutonomousState() {
        // Autonomous state machine logic here
        switch (currentState) {
            case IDLE:
                // Wait for autonomous trigger
                break;
            case MOVING_TO_TARGET:
                // Autonomous movement logic
                break;
            case COMPLETED:
                // Autonomous completion logic
                break;
        }
    }

    private void checkSafetyConditions() {
        // Check for safety violations
        if (distanceSensor != null && distanceSensor.getDistance(DistanceUnit.CM) < 5.0) {
            // Too close to obstacle - stop
            setDrivePowers(0.0, 0.0);
        }
    }

    // Hardware control - focused methods
    private void controlHardware() {
        // Hardware control is handled in updateRobotState()
        // This method is for any additional hardware control
    }

    private void setDrivePowers(double leftPower, double rightPower) {
        if (leftDrive != null) leftDrive.setPower(leftPower);
        if (rightDrive != null) rightDrive.setPower(rightPower);
    }

    private void openClaw() {
        if (clawServo != null) {
            clawServo.setPosition(CLAW_OPEN_POSITION);
            isClawOpen = true;
        }
    }

    private void closeClaw() {
        if (clawServo != null) {
            clawServo.setPosition(CLAW_CLOSED_POSITION);
            isClawOpen = false;
        }
    }

    // Autonomous control methods
    private void startAutonomousSequence() {
        currentState = RobotState.MOVING_TO_TARGET;
        telemetry.addData("Status", "Autonomous sequence started");
    }

    private void stopAutonomousSequence() {
        currentState = RobotState.IDLE;
        telemetry.addData("Status", "Autonomous sequence stopped");
    }

    // Telemetry updates - organized and clear
    private void updateTelemetry() {
        displayRobotStatus();
        displaySensorData();
        displayPerformanceMetrics();
    }

    private void displayRobotStatus() {
        telemetry.addData("Robot State", currentState);
        telemetry.addData("Arm Position", String.format("%.2f", targetArmPosition));
        telemetry.addData("Claw Status", isClawOpen ? "Open" : "Closed");
    }

    private void displaySensorData() {
        if (colorSensor != null) {
            telemetry.addData("Color R", colorSensor.red());
            telemetry.addData("Color G", colorSensor.green());
            telemetry.addData("Color B", colorSensor.blue());
        }

        if (distanceSensor != null) {
            telemetry.addData("Distance (cm)", String.format("%.1f", distanceSensor.getDistance(DistanceUnit.CM)));
        }
    }

    private void displayPerformanceMetrics() {
        telemetry.addData("Runtime", String.format("%.1f", getRuntime()));
    }

    private void displayInitializationStatus() {
        telemetry.addData("Status", "Initialization complete");
        telemetry.addData("Hardware Ready", (leftDrive != null && rightDrive != null) ? "Yes" : "No");
    }

    // Enums for clear state management
    private enum RobotState {
        IDLE, MOVING_TO_TARGET, COMPLETED
    }
}

/**
 * Autonomous OpMode for collecting game elements.
 * This OpMode implements a state machine to drive to game elements,
 * collect them using the intake mechanism, and return to the depot.
 * 
 * @author Your Team Name
 * @version 1.0
 */
public class AutonomousCollectorOpMode extends OpMode {

    // Hardware components
    private DcMotor leftDriveMotor, rightDriveMotor;
    private DcMotor intakeMotor;
    private Servo armServo;
    private ColorSensor gameElementDetector;

    // State management
    private AutonomousState currentState = AutonomousState.SEARCHING;
    private double startTime;
    private int collectedElements = 0;

    // Configuration constants
    private static final double DRIVE_SPEED = 0.6;
    private static final double TURN_SPEED = 0.4;
    private static final double INTAKE_SPEED = 0.8;
    private static final double ARM_UP_POSITION = 0.8;
    private static final double ARM_DOWN_POSITION = 0.2;
    private static final int MAX_COLLECTION_TIME_MS = 3000;

    @Override
    public void init() {
        initializeHardware();
        configureRobot();
        startTime = getRuntime();
        telemetry.addData("Status", "Autonomous Collector initialized");
    }

    @Override
    public void loop() {
        updateAutonomousState();
        executeCurrentState();
        updateTelemetry();

        // Safety timeout
        if (getRuntime() - startTime > 30.0) {
            stopAutonomous();
        }
    }

    /**
     * Initializes all hardware components.
     * Maps hardware devices to variables and configures basic settings.
     */
    private void initializeHardware() {
        try {
            // Initialize drive motors
            leftDriveMotor = hardwareMap.get(DcMotor.class, "left_drive");
            rightDriveMotor = hardwareMap.get(DcMotor.class, "right_drive");

            // Initialize collection mechanism
            intakeMotor = hardwareMap.get(DcMotor.class, "intake_motor");
            armServo = hardwareMap.get(Servo.class, "arm_servo");

            // Initialize sensors
            gameElementDetector = hardwareMap.get(ColorSensor.class, "color_sensor");

        } catch (Exception e) {
            telemetry.addData("ERROR", "Hardware initialization failed: " + e.getMessage());
        }
    }

    /**
     * Configures robot settings for autonomous operation.
     * Sets motor directions, zero power behaviors, and initial positions.
     */
    private void configureRobot() {
        // Configure drive motors
        if (leftDriveMotor != null && rightDriveMotor != null) {
            leftDriveMotor.setDirection(DcMotor.Direction.FORWARD);
            rightDriveMotor.setDirection(DcMotor.Direction.REVERSE);
            leftDriveMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
            rightDriveMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        }

        // Configure collection mechanism
        if (armServo != null) {
            armServo.setPosition(ARM_DOWN_POSITION); // Start with arm down
        }
    }

    /**
     * Updates the autonomous state machine based on current conditions.
     * Transitions between states based on sensor readings and timing.
     */
    private void updateAutonomousState() {
        switch (currentState) {
            case SEARCHING:
                // Transition to APPROACHING when game element is detected
                if (isGameElementDetected()) {
                    currentState = AutonomousState.APPROACHING;
                    telemetry.addData("State Change", "SEARCHING -> APPROACHING");
                }
                break;

            case APPROACHING:
                // Transition to COLLECTING when close enough
                if (isCloseToGameElement()) {
                    currentState = AutonomousState.COLLECTING;
                    telemetry.addData("State Change", "APPROACHING -> COLLECTING");
                }
                break;

            case COLLECTING:
                // Transition to RETURNING after collection time or when full
                if (isCollectionComplete()) {
                    currentState = AutonomousState.RETURNING;
                    telemetry.addData("State Change", "COLLECTING -> RETURNING");
                }
                break;

            case RETURNING:
                // Transition to COMPLETED when back at depot
                if (isAtDepot()) {
                    currentState = AutonomousState.COMPLETED;
                    telemetry.addData("State Change", "RETURNING -> COMPLETED");
                }
                break;

            case COMPLETED:
                // Stay in completed state
                break;
        }
    }

    /**
     * Executes the actions for the current autonomous state.
     * Each state has specific motor and servo commands.
     */
    private void executeCurrentState() {
        switch (currentState) {
            case SEARCHING:
                executeSearchingState();
                break;

            case APPROACHING:
                executeApproachingState();
                break;

            case COLLECTING:
                executeCollectingState();
                break;

            case RETURNING:
                executeReturningState();
                break;

            case COMPLETED:
                executeCompletedState();
                break;
        }
    }

    // State execution methods with clear documentation

    /**
     * Executes the searching state - drives slowly while looking for game elements.
     */
    private void executeSearchingState() {
        // Drive slowly in a pattern while looking for game elements
        setDrivePowers(DRIVE_SPEED * 0.3, DRIVE_SPEED * 0.3);
        stopIntake();
    }

    /**
     * Executes the approaching state - drives toward detected game element.
     */
    private void executeApproachingState() {
        // Drive toward the detected game element
        setDrivePowers(DRIVE_SPEED, DRIVE_SPEED);
        stopIntake();
    }

    /**
     * Executes the collecting state - runs intake and lowers arm.
     */
    private void executeCollectingState() {
        // Stop driving and start collection
        setDrivePowers(0.0, 0.0);
        startIntake();
        lowerArm();
    }

    /**
     * Executes the returning state - drives back to depot with collected elements.
     */
    private void executeReturningState() {
        // Drive back to depot
        setDrivePowers(-DRIVE_SPEED, -DRIVE_SPEED);
        stopIntake();
        raiseArm();
    }

    /**
     * Executes the completed state - stops all movement.
     */
    private void executeCompletedState() {
        // Stop all movement
        setDrivePowers(0.0, 0.0);
        stopIntake();
    }

    // Helper methods with clear names and documentation

    /**
     * Checks if a game element is detected by the color sensor.
     * @return true if a game element is detected, false otherwise
     */
    private boolean isGameElementDetected() {
        if (gameElementDetector == null) return false;

        // Check for specific color values that indicate a game element
        int red = gameElementDetector.red();
        int green = gameElementDetector.green();
        int blue = gameElementDetector.blue();

        // Example: detect yellow game elements
        return red > 100 && green > 100 && blue < 50;
    }

    /**
     * Checks if the robot is close enough to a game element to start collection.
     * @return true if close enough, false otherwise
     */
    private boolean isCloseToGameElement() {
        // This would typically use distance sensors
        // For this example, we'll use a simple time-based approach
        return getRuntime() - startTime > 2.0; // Assume close after 2 seconds
    }

    /**
     * Checks if the collection process is complete.
     * @return true if collection is complete, false otherwise
     */
    private boolean isCollectionComplete() {
        // Check if enough time has passed for collection
        return getRuntime() - startTime > 5.0; // 5 seconds for collection
    }

    /**
     * Checks if the robot has returned to the depot.
     * @return true if at depot, false otherwise
     */
    private boolean isAtDepot() {
        // This would typically use encoders or other sensors
        // For this example, we'll use a simple time-based approach
        return getRuntime() - startTime > 8.0; // Assume at depot after 8 seconds
    }

    // Hardware control methods

    private void setDrivePowers(double leftPower, double rightPower) {
        if (leftDriveMotor != null) leftDriveMotor.setPower(leftPower);
        if (rightDriveMotor != null) rightDriveMotor.setPower(rightPower);
    }

    private void startIntake() {
        if (intakeMotor != null) intakeMotor.setPower(INTAKE_SPEED);
    }

    private void stopIntake() {
        if (intakeMotor != null) intakeMotor.setPower(0.0);
    }

    private void raiseArm() {
        if (armServo != null) armServo.setPosition(ARM_UP_POSITION);
    }

    private void lowerArm() {
        if (armServo != null) armServo.setPosition(ARM_DOWN_POSITION);
    }

    private void stopAutonomous() {
        setDrivePowers(0.0, 0.0);
        stopIntake();
        currentState = AutonomousState.COMPLETED;
        telemetry.addData("Status", "Autonomous stopped due to timeout");
    }

    private void updateTelemetry() {
        telemetry.addData("Autonomous State", currentState);
        telemetry.addData("Runtime", String.format("%.1f", getRuntime()));
        telemetry.addData("Collected Elements", collectedElements);

        if (gameElementDetector != null) {
            telemetry.addData("Color R", gameElementDetector.red());
            telemetry.addData("Color G", gameElementDetector.green());
            telemetry.addData("Color B", gameElementDetector.blue());
        }
    }

    /**
     * Enumeration of autonomous states for clear state management.
     */
    private enum AutonomousState {
        SEARCHING,    // Looking for game elements
        APPROACHING,  // Driving toward detected element
        COLLECTING,   // Collecting the game element
        RETURNING,    // Driving back to depot
        COMPLETED     // Autonomous sequence finished
    }
}

