// Define the starting pose
Pose2d startPose = new Pose2d(0, 0, 0);

// Create a trajectory builder
Trajectory trajectory = drive.trajectoryBuilder(startPose)
    .forward(24) // Move forward 24 inches
    .build();

// First trajectory
Trajectory traj1 = drive.trajectoryBuilder(startPose)
    .forward(24)
    .build();

// Second trajectory, starting from the end of the first
Trajectory traj2 = drive.trajectoryBuilder(traj1.end())
    .strafeLeft(12)
    .build();

// Follow both trajectories
drive.followTrajectory(traj1);
drive.followTrajectory(traj2);
