package org.firstinspires.ftc.teamcode;

import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.eventloop.opmode.Autonomous;
import com.qualcomm.robotcore.hardware.DcMotor;
import com.qualcomm.robotcore.hardware.Servo;

@Autonomous(name="Linear Example", group="Examples")
public class LinearExample extends LinearOpMode {

    private DcMotor leftMotor, rightMotor;
    private Servo armServo;

    @Override
    public void runOpMode() {
        // Initialize hardware
        initHardware();

        telemetry.addData("Status", "Ready for start");
        telemetry.update();

        waitForStart();

        // Linear execution - each step happens in sequence

        // Step 1: Move forward for 2 seconds
        telemetry.addData("Step", "1 - Moving forward");
        telemetry.update();

        leftMotor.setPower(0.5);
        rightMotor.setPower(0.5);
        sleep(2000); // Wait for 2 seconds

        // Step 2: Stop motors
        telemetry.addData("Step", "2 - Stopping");
        telemetry.update();

        leftMotor.setPower(0);
        rightMotor.setPower(0);
        sleep(500); // Wait for 0.5 seconds

        // Step 3: Raise arm
        telemetry.addData("Step", "3 - Raising arm");
        telemetry.update();

        armServo.setPosition(1.0);
        sleep(1000); // Wait for 1 second

        // Step 4: Turn right
        telemetry.addData("Step", "4 - Turning right");
        telemetry.update();

        leftMotor.setPower(0.5);
        rightMotor.setPower(-0.5);
        sleep(1500); // Wait for 1.5 seconds

        // Step 5: Stop and lower arm
        telemetry.addData("Step", "5 - Final position");
        telemetry.update();

        leftMotor.setPower(0);
        rightMotor.setPower(0);
        armServo.setPosition(0.0);
        sleep(1000);

        telemetry.addData("Status", "Autonomous complete");
        telemetry.update();
    }

    private void initHardware() {
        leftMotor = hardwareMap.get(DcMotor.class, "left_motor");
        rightMotor = hardwareMap.get(DcMotor.class, "right_motor");
        armServo = hardwareMap.get(Servo.class, "arm_servo");

        leftMotor.setDirection(DcMotor.Direction.FORWARD);
        rightMotor.setDirection(DcMotor.Direction.REVERSE);

        leftMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        rightMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
    }
}

package org.firstinspires.ftc.teamcode;

import com.qualcomm.robotcore.eventloop.opmode.IterativeOpMode;
import com.qualcomm.robotcore.eventloop.opmode.TeleOp;
import com.qualcomm.robotcore.hardware.DcMotor;
import com.qualcomm.robotcore.hardware.Servo;
import com.qualcomm.robotcore.util.ElapsedTime;

@TeleOp(name="Iterative Example", group="Examples")
public class IterativeExample extends IterativeOpMode {

    private DcMotor leftMotor, rightMotor;
    private Servo armServo;
    private ElapsedTime runtime;

    // State variables
    private double armPosition = 0.5;
    private boolean armMoving = false;
    private long lastArmUpdate = 0;
    private static final long ARM_UPDATE_INTERVAL = 50; // 50ms between updates

    @Override
    public void init() {
        // Initialize hardware
        initHardware();
        runtime = new ElapsedTime();

        telemetry.addData("Status", "Initialized");
        telemetry.update();
    }

    @Override
    public void init_loop() {
        // This runs while waiting for start
        telemetry.addData("Status", "Waiting for start...");
        telemetry.addData("Runtime", "%.1f seconds", runtime.seconds());
        telemetry.update();
    }

    @Override
    public void start() {
        // This runs once when start is pressed
        runtime.reset();
        telemetry.addData("Status", "Started");
        telemetry.update();
    }

    @Override
    public void loop() {
        // This runs continuously during the OpMode

        // Process drive input
        processDriveInput();

        // Process arm input with timing control
        processArmInput();

        // Update telemetry
        updateTelemetry();

        // Optional: Add a small delay to prevent overwhelming the system
        // This is manual control - you decide the timing
        try {
            Thread.sleep(10); // 10ms delay
        } catch (InterruptedException e) {
            // Handle interruption if needed
        }
    }

    @Override
    public void stop() {
        // This runs when the OpMode is stopped
        leftMotor.setPower(0);
        rightMotor.setPower(0);

        telemetry.addData("Status", "Stopped");
        telemetry.update();
    }

    private void initHardware() {
        leftMotor = hardwareMap.get(DcMotor.class, "left_motor");
        rightMotor = hardwareMap.get(DcMotor.class, "right_motor");
        armServo = hardwareMap.get(Servo.class, "arm_servo");

        leftMotor.setDirection(DcMotor.Direction.FORWARD);
        rightMotor.setDirection(DcMotor.Direction.REVERSE);

        leftMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        rightMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);

        armServo.setPosition(armPosition);
    }

    private void processDriveInput() {
        // Get gamepad input
        double leftPower = gamepad1.left_stick_y;
        double rightPower = gamepad1.right_stick_y;

        // Apply dead zone
        leftPower = applyDeadZone(leftPower, 0.1);
        rightPower = applyDeadZone(rightPower, 0.1);

        // Set motor powers
        leftMotor.setPower(leftPower);
        rightMotor.setPower(rightPower);
    }

    private void processArmInput() {
        // Check if enough time has passed since last update
        long currentTime = System.currentTimeMillis();
        if (currentTime - lastArmUpdate < ARM_UPDATE_INTERVAL) {
            return; // Skip this update
        }

        // Process arm controls
        if (gamepad1.right_trigger > 0.1) {
            // Move arm up
            armPosition = Math.min(armPosition + 0.01, 1.0);
            armMoving = true;
        } else if (gamepad1.left_trigger > 0.1) {
            // Move arm down
            armPosition = Math.max(armPosition - 0.01, 0.0);
            armMoving = true;
        } else {
            armMoving = false;
        }

        // Update servo position
        armServo.setPosition(armPosition);
        lastArmUpdate = currentTime;
    }

    private double applyDeadZone(double value, double deadZone) {
        if (Math.abs(value) < deadZone) {
            return 0.0;
        }
        return value;
    }

    private void updateTelemetry() {
        telemetry.addData("Runtime", "%.1f seconds", runtime.seconds());
        telemetry.addData("Left Motor Power", "%.2f", leftMotor.getPower());
        telemetry.addData("Right Motor Power", "%.2f", rightMotor.getPower());
        telemetry.addData("Arm Position", "%.2f", armPosition);
        telemetry.addData("Arm Moving", armMoving ? "Yes" : "No");
        telemetry.update();
    }
}

// LINEAR OPMODE - Simple, sequential execution
@Autonomous(name="Linear Auto", group="Auto")
public class LinearAuto extends LinearOpMode {

    @Override
    public void runOpMode() {
        waitForStart();

        // Step 1: Move forward
        driveForward(0.5, 2000);

        // Step 2: Turn
        turnRight(0.5, 1000);

        // Step 3: Move forward again
        driveForward(0.5, 1500);

        // Done - automatic timing
    }

    private void driveForward(double power, long timeMs) {
        leftMotor.setPower(power);
        rightMotor.setPower(power);
        sleep(timeMs); // Automatic delay
        leftMotor.setPower(0);
        rightMotor.setPower(0);
    }

    private void turnRight(double power, long timeMs) {
        leftMotor.setPower(power);
        rightMotor.setPower(-power);
        sleep(timeMs); // Automatic delay
        leftMotor.setPower(0);
        rightMotor.setPower(0);
    }
}

// ITERATIVE OPMODE - Complex, real-time control
@TeleOp(name="Iterative TeleOp", group="TeleOp")
public class IterativeTeleOp extends IterativeOpMode {

    private ElapsedTime runtime;
    private boolean autoDriveEnabled = false;
    private double targetHeading = 0;

    @Override
    public void init() {
        runtime = new ElapsedTime();
        // Initialize hardware
    }

    @Override
    public void loop() {
        // Real-time input processing
        processGamepadInput();

        // Complex state machine
        if (autoDriveEnabled) {
            maintainHeading();
        }

        // Continuous sensor monitoring
        monitorSensors();

        // Manual timing control
        if (runtime.milliseconds() % 50 == 0) { // Update every 50ms
            updateTelemetry();
        }
    }

    private void processGamepadInput() {
        // Immediate response to input
        if (gamepad1.a) {
            autoDriveEnabled = !autoDriveEnabled;
        }

        // Complex input processing
        double drive = gamepad1.left_stick_y;
        double turn = gamepad1.right_stick_x;

        // Apply advanced control algorithms
        applyDriveControl(drive, turn);
    }

    private void maintainHeading() {
        // PID control for heading maintenance
        double currentHeading = imu.getAngularOrientation().firstAngle;
        double error = targetHeading - currentHeading;

        // Apply PID correction
        double correction = calculatePIDCorrection(error);
        applyCorrection(correction);
    }

    private void monitorSensors() {
        // Continuous sensor monitoring
        if (distanceSensor.getDistance(DistanceUnit.CM) < 10) {
            // Obstacle detected - take action
            emergencyStop();
        }
    }
}

@Autonomous(name="Linear Best Practices", group="Auto")
public class LinearBestPractices extends LinearOpMode {

    private DcMotor leftMotor, rightMotor;
    private Servo armServo;

    // Constants for better maintainability
    private static final double DRIVE_POWER = 0.5;
    private static final double TURN_POWER = 0.3;
    private static final long FORWARD_TIME = 2000;
    private static final long TURN_TIME = 1000;

    @Override
    public void runOpMode() {
        initHardware();

        telemetry.addData("Status", "Ready");
        telemetry.update();

        waitForStart();

        // Use descriptive method names
        driveForward();
        turnRight();
        raiseArm();
        driveForward();
        lowerArm();

        telemetry.addData("Status", "Complete");
        telemetry.update();
    }

    private void initHardware() {
        leftMotor = hardwareMap.get(DcMotor.class, "left_motor");
        rightMotor = hardwareMap.get(DcMotor.class, "right_motor");
        armServo = hardwareMap.get(Servo.class, "arm_servo");

        // Configure hardware
        configureMotors();
        configureServo();
    }

    private void configureMotors() {
        leftMotor.setDirection(DcMotor.Direction.FORWARD);
        rightMotor.setDirection(DcMotor.Direction.REVERSE);
        leftMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        rightMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
    }

    private void configureServo() {
        armServo.setDirection(Servo.Direction.FORWARD);
        armServo.setPosition(0.0); // Start position
    }

    private void driveForward() {
        telemetry.addData("Action", "Driving forward");
        telemetry.update();

        leftMotor.setPower(DRIVE_POWER);
        rightMotor.setPower(DRIVE_POWER);
        sleep(FORWARD_TIME);

        stopMotors();
    }

    private void turnRight() {
        telemetry.addData("Action", "Turning right");
        telemetry.update();

        leftMotor.setPower(TURN_POWER);
        rightMotor.setPower(-TURN_POWER);
        sleep(TURN_TIME);

        stopMotors();
    }

    private void raiseArm() {
        telemetry.addData("Action", "Raising arm");
        telemetry.update();

        armServo.setPosition(1.0);
        sleep(1000); // Wait for servo to reach position
    }

    private void lowerArm() {
        telemetry.addData("Action", "Lowering arm");
        telemetry.update();

        armServo.setPosition(0.0);
        sleep(1000); // Wait for servo to reach position
    }

    private void stopMotors() {
        leftMotor.setPower(0);
        rightMotor.setPower(0);
        sleep(200); // Brief pause after stopping
    }
}

@TeleOp(name="Iterative Best Practices", group="TeleOp")
public class IterativeBestPractices extends IterativeOpMode {

    private DcMotor leftMotor, rightMotor;
    private Servo armServo;
    private ColorSensor colorSensor;
    private BNO055IMU imu;

    private ElapsedTime runtime;
    private ElapsedTime telemetryTimer;

    // State variables
    private double armPosition = 0.5;
    private boolean autoDriveEnabled = false;
    private double targetHeading = 0;
    private int driveMode = 0; // 0 = tank, 1 = arcade, 2 = mecanum

    // Constants
    private static final double ARM_SPEED = 0.01;
    private static final long TELEMETRY_INTERVAL = 50; // 50ms
    private static final double DEAD_ZONE = 0.1;

    @Override
    public void init() {
        runtime = new ElapsedTime();
        telemetryTimer = new ElapsedTime();

        initHardware();

        telemetry.addData("Status", "Initialized");
        telemetry.update();
    }

    @Override
    public void init_loop() {
        // Show initialization progress
        telemetry.addData("Status", "Waiting for start...");
        telemetry.addData("Runtime", "%.1f seconds", runtime.seconds());
        telemetry.update();
    }

    @Override
    public void start() {
        runtime.reset();
        telemetryTimer.reset();

        telemetry.addData("Status", "Started");
        telemetry.update();
    }

    @Override
    public void loop() {
        // Process all inputs
        processDriveInput();
        processArmInput();
        processModeInput();

        // Update systems
        updateAutoDrive();
        updateColorDetection();

        // Update telemetry at regular intervals
        if (telemetryTimer.milliseconds() >= TELEMETRY_INTERVAL) {
            updateTelemetry();
            telemetryTimer.reset();
        }
    }

    @Override
    public void stop() {
        // Cleanup
        leftMotor.setPower(0);
        rightMotor.setPower(0);

        telemetry.addData("Status", "Stopped");
        telemetry.update();
    }

    private void initHardware() {
        // Map hardware
        leftMotor = hardwareMap.get(DcMotor.class, "left_motor");
        rightMotor = hardwareMap.get(DcMotor.class, "right_motor");
        armServo = hardwareMap.get(Servo.class, "arm_servo");
        colorSensor = hardwareMap.get(ColorSensor.class, "color_sensor");
        imu = hardwareMap.get(BNO055IMU.class, "imu");

        // Configure hardware
        configureMotors();
        configureServo();
        configureSensors();
    }

    private void configureMotors() {
        leftMotor.setDirection(DcMotor.Direction.FORWARD);
        rightMotor.setDirection(DcMotor.Direction.REVERSE);
        leftMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        rightMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
    }

    private void configureServo() {
        armServo.setDirection(Servo.Direction.FORWARD);
        armServo.setPosition(armPosition);
    }

    private void configureSensors() {
        colorSensor.enableLed(true);

        BNO055IMU.Parameters parameters = new BNO055IMU.Parameters();
        parameters.angleUnit = BNO055IMU.AngleUnit.DEGREES;
        parameters.accelUnit = BNO055IMU.AccelUnit.METERS_PERSEC_PERSEC;
        imu.initialize(parameters);
    }

    private void processDriveInput() {
        double leftPower, rightPower;

        switch (driveMode) {
            case 0: // Tank drive
                leftPower = applyDeadZone(gamepad1.left_stick_y, DEAD_ZONE);
                rightPower = applyDeadZone(gamepad1.right_stick_y, DEAD_ZONE);
                break;
            case 1: // Arcade drive
                double drive = applyDeadZone(gamepad1.left_stick_y, DEAD_ZONE);
                double turn = applyDeadZone(gamepad1.right_stick_x, DEAD_ZONE);
                leftPower = drive + turn;
                rightPower = drive - turn;
                break;
            default: // Mecanum drive
                double forward = applyDeadZone(gamepad1.left_stick_y, DEAD_ZONE);
                double strafe = applyDeadZone(gamepad1.left_stick_x, DEAD_ZONE);
                double rotate = applyDeadZone(gamepad1.right_stick_x, DEAD_ZONE);
                leftPower = forward + strafe + rotate;
                rightPower = forward - strafe - rotate;
                break;
        }

        // Apply power limits
        leftPower = Math.max(-1.0, Math.min(1.0, leftPower));
        rightPower = Math.max(-1.0, Math.min(1.0, rightPower));

        leftMotor.setPower(leftPower);
        rightMotor.setPower(rightPower);
    }

    private void processArmInput() {
        // Arm control with triggers
        if (gamepad1.right_trigger > 0.1) {
            armPosition = Math.min(armPosition + ARM_SPEED, 1.0);
        } else if (gamepad1.left_trigger > 0.1) {
            armPosition = Math.max(armPosition - ARM_SPEED, 0.0);
        }

        armServo.setPosition(armPosition);
    }

    private void processModeInput() {
        // Mode switching
        if (gamepad1.a) {
            driveMode = 0; // Tank drive
        } else if (gamepad1.b) {
            driveMode = 1; // Arcade drive
        } else if (gamepad1.x) {
            driveMode = 2; // Mecanum drive
        }

        // Auto drive toggle
        if (gamepad1.y) {
            autoDriveEnabled = !autoDriveEnabled;
            if (autoDriveEnabled) {
                targetHeading = imu.getAngularOrientation().firstAngle;
            }
        }
    }

    private void updateAutoDrive() {
        if (!autoDriveEnabled) return;

        double currentHeading = imu.getAngularOrientation().firstAngle;
        double error = targetHeading - currentHeading;

        // Simple P control for heading maintenance
        double correction = error * 0.01;

        // Apply correction
        double currentLeftPower = leftMotor.getPower();
        double currentRightPower = rightMotor.getPower();

        leftMotor.setPower(currentLeftPower + correction);
        rightMotor.setPower(currentRightPower - correction);
    }

    private void updateColorDetection() {
        int red = colorSensor.red();
        int green = colorSensor.green();
        int blue = colorSensor.blue();

        // Store color data for telemetry
        // Could trigger actions based on color
    }

    private double applyDeadZone(double value, double deadZone) {
        if (Math.abs(value) < deadZone) {
            return 0.0;
        }
        return value;
    }

    private void updateTelemetry() {
        telemetry.addData("Runtime", "%.1f seconds", runtime.seconds());
        telemetry.addData("Drive Mode", getDriveModeName());
        telemetry.addData("Auto Drive", autoDriveEnabled ? "ON" : "OFF");
        telemetry.addData("Arm Position", "%.2f", armPosition);
        telemetry.addData("Heading", "%.1fÂ°", imu.getAngularOrientation().firstAngle);
        telemetry.update();
    }

    private String getDriveModeName() {
        switch (driveMode) {
            case 0: return "Tank";
            case 1: return "Arcade";
            case 2: return "Mecanum";
            default: return "Unknown";
        }
    }
}

