// CONVERTED TO ITERATIVE OPMODE
@Autonomous(name="Iterative Converted", group="Auto")
public class IterativeConverted extends IterativeOpMode {

    private ElapsedTime runtime;
    private int currentStep = 0;
    private boolean stepComplete = false;

    // Step timing
    private long stepStartTime = 0;
    private static final long FORWARD_TIME = 2000;
    private static final long TURN_TIME = 1000;

    @Override
    public void init() {
        runtime = new ElapsedTime();
        // Initialize hardware
    }

    @Override
    public void loop() {
        // Manual state machine
        switch (currentStep) {
            case 0:
                if (!stepComplete) {
                    startDriveForward();
                } else if (runtime.milliseconds() - stepStartTime >= FORWARD_TIME) {
                    completeStep();
                }
                break;
            case 1:
                if (!stepComplete) {
                    startTurnRight();
                } else if (runtime.milliseconds() - stepStartTime >= TURN_TIME) {
                    completeStep();
                }
                break;
            case 2:
                if (!stepComplete) {
                    startDriveForward();
                } else if (runtime.milliseconds() - stepStartTime >= FORWARD_TIME) {
                    completeStep();
                }
                break;
            default:
                // Autonomous complete
                stopMotors();
                break;
        }
    }

    private void startDriveForward() {
        leftMotor.setPower(0.5);
        rightMotor.setPower(0.5);
        stepStartTime = runtime.milliseconds();
        stepComplete = false;
    }

    private void startTurnRight() {
        leftMotor.setPower(0.5);
        rightMotor.setPower(-0.5);
        stepStartTime = runtime.milliseconds();
        stepComplete = false;
    }

    private void completeStep() {
        stopMotors();
        stepComplete = true;
        currentStep++;
    }

    private void stopMotors() {
        leftMotor.setPower(0);
        rightMotor.setPower(0);
    }
}

