import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.eventloop.opmode.TeleOp;
import com.qualcomm.robotcore.hardware.DcMotor;

@TeleOp(name = "LinearMotorControl")
public class LinearMotorControl extends LinearOpMode {
    private DcMotor driveMotor;

    @Override
    public void runOpMode() {
        // Initialize hardware
        driveMotor = hardwareMap.get(DcMotor.class, "drive_motor");

        // Show initialization status
        telemetry.addData("Status", "Initialized");
        telemetry.update();

        // Wait for game to start
        waitForStart();

        // Main loop - runs sequentially
        while (opModeIsActive()) {
            // Read gamepad input
            double power = -gamepad1.left_stick_y;

            // Drive motor
            driveMotor.setPower(power);

            // Show telemetry
            telemetry.addData("Motor Power", power);
            telemetry.addData("Status", "Running");
            telemetry.update();
        }
    }
}

import com.qualcomm.robotcore.eventloop.opmode.OpMode;
import com.qualcomm.robotcore.eventloop.opmode.TeleOp;
import com.qualcomm.robotcore.hardware.DcMotor;

@TeleOp(name = "IterativeMotorControl")
public class IterativeMotorControl extends OpMode {
    private DcMotor driveMotor;

    @Override
    public void init() {
        // Initialize hardware - called once at start
        driveMotor = hardwareMap.get(DcMotor.class, "drive_motor");

        // Show initialization status
        telemetry.addData("Status", "Initialized");
        telemetry.update();
    }

    @Override
    public void loop() {
        // Main loop - called repeatedly
        // Read gamepad input
        double power = -gamepad1.left_stick_y;

        // Drive motor
        driveMotor.setPower(power);

        // Show telemetry
        telemetry.addData("Motor Power", power);
        telemetry.addData("Status", "Running");
        telemetry.update();
    }
}

// Key Differences:
//
// LINEAR OPMODE:
// - Extends LinearOpMode
// - All code in runOpMode() method
// - Runs sequentially like a script
// - Can use waitForStart() and sleep()
// - Good for beginners and simple routines
//
// ITERATIVE OPMODE:
// - Extends OpMode
// - Separate init() and loop() methods
// - init() runs once, loop() runs repeatedly
// - More control over timing and state
// - Better for advanced TeleOp and state machines
//
// Both achieve the same result but with different structures!
