import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.eventloop.opmode.TeleOp;
import com.qualcomm.robotcore.hardware.DcMotor;

@TeleOp(name = "MotorControl")
public class MotorControl extends LinearOpMode {
    private DcMotor driveMotor;

    @Override
    public void runOpMode() {
        // Initialize hardware
        driveMotor = hardwareMap.get(DcMotor.class, "drive_motor");

        // Wait for start
        waitForStart();

        while (opModeIsActive()) {
            // Drive motor with left stick Y-axis
            double power = -gamepad1.left_stick_y;
            driveMotor.setPower(power);
        }
    }
}

// Add this inside the while loop
while (opModeIsActive()) {
    double power = -gamepad1.left_stick_y;
    driveMotor.setPower(power);

    // Display motor power on Driver Station
    telemetry.addData("Motor Power", power);
    telemetry.addData("Status", "Running");
    telemetry.update();
}

import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.eventloop.opmode.Autonomous;
import com.qualcomm.robotcore.hardware.DcMotor;

@Autonomous(name = "SimpleAuto")
public class SimpleAuto extends LinearOpMode {
    private DcMotor leftMotor, rightMotor;

    @Override
    public void runOpMode() {
        // Initialize hardware
        leftMotor = hardwareMap.get(DcMotor.class, "left_motor");
        rightMotor = hardwareMap.get(DcMotor.class, "right_motor");

        telemetry.addData("Status", "Initialized");
        telemetry.update();

        waitForStart();

        // Drive forward for 2 seconds
        leftMotor.setPower(0.5);
        rightMotor.setPower(0.5);
        sleep(2000);

        // Stop
        leftMotor.setPower(0);
        rightMotor.setPower(0);
    }
}

import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.eventloop.opmode.TeleOp;
import com.qualcomm.robotcore.hardware.DcMotor;

@TeleOp(name = "OrganizedOpMode")
public class OrganizedOpMode extends LinearOpMode {
    private DcMotor leftMotor, rightMotor;

    @Override
    public void runOpMode() {
        initHardware();
        showInitTelemetry();
        waitForStart();

        while (opModeIsActive()) {
            processInputs();
            updateTelemetry();
        }
    }

    private void initHardware() {
        leftMotor = hardwareMap.get(DcMotor.class, "left_motor");
        rightMotor = hardwareMap.get(DcMotor.class, "right_motor");
    }

    private void showInitTelemetry() {
        telemetry.addData("Status", "Hardware Initialized");
        telemetry.update();
    }

    private void processInputs() {
        double leftPower = -gamepad1.left_stick_y;
        double rightPower = -gamepad1.right_stick_y;
        leftMotor.setPower(leftPower);
        rightMotor.setPower(rightPower);
    }

    private void updateTelemetry() {
        telemetry.addData("Left Power", leftMotor.getPower());
        telemetry.addData("Right Power", rightMotor.getPower());
        telemetry.update();
    }
}
