// Basic power control
motor.setPower(1.0);   // Full speed forward
motor.setPower(0.5);   // Half speed forward
motor.setPower(0.0);   // Stop
motor.setPower(-0.5);  // Half speed reverse
motor.setPower(-1.0);  // Full speed reverse

// Power control with variables
double power = 0.75;
motor.setPower(power);

// Conditional power control
if (gamepad1.a) {
    motor.setPower(1.0);
} else if (gamepad1.b) {
    motor.setPower(-1.0);
} else {
    motor.setPower(0.0);
}

// Method 1: Set motor direction globally
motor.setDirection(DcMotor.Direction.FORWARD);
motor.setPower(0.5); // Always moves forward

motor.setDirection(DcMotor.Direction.REVERSE);
motor.setPower(0.5); // Always moves in reverse direction

// Method 2: Use negative power for direction control
motor.setDirection(DcMotor.Direction.FORWARD);
motor.setPower(0.5);  // Forward
motor.setPower(-0.5); // Reverse

// Common pattern for drivetrains
leftMotor.setDirection(DcMotor.Direction.FORWARD);
rightMotor.setDirection(DcMotor.Direction.REVERSE);
// Now positive power on both motors makes the robot go forward

// Set zero power behavior
motor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
// Good for: Drivetrains, mechanisms that need to hold position

motor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.FLOAT);
// Good for: Mechanisms that should coast, reducing wear

// Example: Different behaviors for different motors
leftDrive.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
rightDrive.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
intakeMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.FLOAT);

// Simple power control (no encoder)
motor.setMode(DcMotor.RunMode.RUN_WITHOUT_ENCODER);
motor.setPower(0.5);

// Encoder-based speed control
motor.setMode(DcMotor.RunMode.RUN_USING_ENCODER);
motor.setPower(0.5); // Speed will be regulated

// Position control
motor.setMode(DcMotor.RunMode.STOP_AND_RESET_ENCODER);
motor.setTargetPosition(1000);
motor.setMode(DcMotor.RunMode.RUN_TO_POSITION);
motor.setPower(0.5); // Move to position

// Check if motor is busy (for position control)
while (motor.isBusy()) {
    telemetry.addData("Position", motor.getCurrentPosition());
    telemetry.update();
}

// Tank Drive (Differential Drive)
public void tankDrive(double leftPower, double rightPower) {
    leftDrive.setPower(leftPower);
    rightDrive.setPower(rightPower);
}

// Arcade Drive
public void arcadeDrive(double drive, double turn) {
    double leftPower = drive + turn;
    double rightPower = drive - turn;
    leftPower = Math.max(-1.0, Math.min(1.0, leftPower));
    rightPower = Math.max(-1.0, Math.min(1.0, rightPower));
    leftDrive.setPower(leftPower);
    rightDrive.setPower(rightPower);
}

// Gamepad control example
while (opModeIsActive()) {
    // Tank drive
    double leftPower = -gamepad1.left_stick_y;
    double rightPower = -gamepad1.right_stick_y;
    tankDrive(leftPower, rightPower);
    // Or arcade drive
    double drive = -gamepad1.left_stick_y;
    double turn = gamepad1.right_stick_x;
    arcadeDrive(drive, turn);
}

// Power limiting utility
public double limitPower(double power) {
    return Math.max(-1.0, Math.min(1.0, power));
}
// Safe motor control method
public void setMotorPowerSafely(DcMotor motor, double power) {
    power = limitPower(power);
    if (getBatteryVoltage() < 11.0) {
        power *= 0.5; // Reduce power if battery is low
    }
    motor.setPower(power);
}
// Emergency stop
public void emergencyStop() {
    leftDrive.setPower(0);
    rightDrive.setPower(0);
    telemetry.addData("STATUS", "EMERGENCY STOP");
    telemetry.update();
}

