// Practice Exercise: Tank Drive
// Create an OpMode that implements tank drive control
// Include safety features and telemetry
// Test different control schemes

@TeleOp(name="Tank Drive Test", group="Testing")
public class TankDriveTestOpMode extends LinearOpMode {
    private DcMotor leftDrive, rightDrive;
    @Override
    public void runOpMode() {
        // Initialize motors
        leftDrive = hardwareMap.get(DcMotor.class, "left_drive");
        rightDrive = hardwareMap.get(DcMotor.class, "right_drive");
        // Configure motors
        leftDrive.setDirection(DcMotor.Direction.FORWARD);
        rightDrive.setDirection(DcMotor.Direction.REVERSE);
        leftDrive.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        rightDrive.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);
        waitForStart();
        while (opModeIsActive()) {
            // Tank drive control
            double leftPower = limitPower(-gamepad1.left_stick_y);
            double rightPower = limitPower(-gamepad1.right_stick_y);
            leftDrive.setPower(leftPower);
            rightDrive.setPower(rightPower);
            // Telemetry
            telemetry.addData("Left Power", leftPower);
            telemetry.addData("Right Power", rightPower);
            telemetry.addData("Battery", "%.1f volts", getBatteryVoltage());
            telemetry.update();
        }
    }
    private double limitPower(double power) {
        return Math.max(-1.0, Math.min(1.0, power));
    }
