// Neverest 20: ~3000 RPM, low torque - Good for fast movement
// Neverest 40: ~1600 RPM, medium torque - Balanced performance
// Neverest 60: ~1050 RPM, high torque - Good for heavy loads
// REV HD Hex: Variable RPM, high torque - High precision control
// GoBilda: Many options - Check datasheet for specific specs

// Motor selection depends on your application:
// - Drivetrain: Usually Neverest 40 or REV HD Hex
// - Lifts/Arms: Usually Neverest 60 or REV HD Hex
// - Intakes: Usually Neverest 20 or 40

// Motor configuration in hardware
DcMotor leftFront = hardwareMap.get(DcMotor.class, "left_front");
DcMotor rightFront = hardwareMap.get(DcMotor.class, "right_front");
DcMotor leftRear = hardwareMap.get(DcMotor.class, "left_rear");
DcMotor rightRear = hardwareMap.get(DcMotor.class, "right_rear");

// Set motor directions (important for proper movement)
leftFront.setDirection(DcMotor.Direction.FORWARD);
rightFront.setDirection(DcMotor.Direction.REVERSE);
leftRear.setDirection(DcMotor.Direction.FORWARD);
rightRear.setDirection(DcMotor.Direction.REVERSE);

// Set motor modes
leftFront.setMode(DcMotor.RunMode.RUN_WITHOUT_ENCODER);
rightFront.setMode(DcMotor.RunMode.RUN_WITHOUT_ENCODER);
leftRear.setMode(DcMotor.RunMode.RUN_WITHOUT_ENCODER);
rightRear.setMode(DcMotor.RunMode.RUN_WITHOUT_ENCODER);

// Basic power control
motor.setPower(0.5); // 50% forward power
motor.setPower(-0.3); // 30% reverse power
motor.setPower(0.0); // Stop

// Encoder-based control
motor.setMode(DcMotor.RunMode.STOP_AND_RESET_ENCODER);
motor.setTargetPosition(1000);
motor.setMode(DcMotor.RunMode.RUN_TO_POSITION);
motor.setPower(0.5);

// Velocity control
motor.setMode(DcMotor.RunMode.RUN_USING_ENCODER);
motor.setPower(0.5); // Power will be regulated for consistent speed

// Zero power behavior
motor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE); // Stop immediately
motor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.FLOAT); // Coast to stop

// Power limiting for safety
public void setMotorPowerSafely(DcMotor motor, double power) {
    // Limit power to safe range
    power = Math.max(-1.0, Math.min(1.0, power));

    // Additional safety checks
    if (getBatteryVoltage() < 11.0) {
        power *= 0.5; // Reduce power if battery is low
    }

    motor.setPower(power);
}

// Emergency stop functionality
public void emergencyStop() {
    // Stop all motors immediately
    leftFront.setPower(0);
    rightFront.setPower(0);
    leftRear.setPower(0);
    rightRear.setPower(0);

    telemetry.addData("STATUS", "EMERGENCY STOP ACTIVATED");
    telemetry.update();
}

// Practice Exercise: Motor Control
// Create an OpMode that allows testing of motor functionality
// Include safety features and telemetry display
// Test different power levels and directions

@TeleOp(name="Motor Test", group="Testing")
public class MotorTestOpMode extends LinearOpMode {

    private DcMotor testMotor;

    @Override
    public void runOpMode() {
        // Initialize motor
        testMotor = hardwareMap.get(DcMotor.class, "test_motor");
        testMotor.setDirection(DcMotor.Direction.FORWARD);
        testMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.BRAKE);

        waitForStart();

        while (opModeIsActive()) {
            // Control motor with gamepad
            double power = -gamepad1.left_stick_y;
            testMotor.setPower(power);

            // Display telemetry
            telemetry.addData("Motor Power", power);
            telemetry.addData("Battery Voltage", "%.1f volts", getBatteryVoltage());
            telemetry.update();
        }
    }
}
