// Accessing hardware components through the hardwareMap
DcMotor leftDrive = hardwareMap.get(DcMotor.class, "left_drive");
DcMotor rightDrive = hardwareMap.get(DcMotor.class, "right_drive");
Servo clawServo = hardwareMap.get(Servo.class, "claw_servo");
TouchSensor touchSensor = hardwareMap.get(TouchSensor.class, "touch_sensor");
ColorSensor colorSensor = hardwareMap.get(ColorSensor.class, "color_sensor");
DistanceSensor distanceSensor = hardwareMap.get(DistanceSensor.class, "distance_sensor");
BNO055IMU imu = hardwareMap.get(BNO055IMU.class, "imu");

// Monitor battery voltage during operation
while (opModeIsActive()) {
    double batteryVoltage = getBatteryVoltage();
    telemetry.addData("Battery", "%.1f volts", batteryVoltage);
    if (batteryVoltage < 11.0) {
        telemetry.addData("WARNING", "Low battery!");
    }
    telemetry.update();
}
private double getBatteryVoltage() {
    return hardwareMap.voltageSensor.iterator().next().getVoltage();
}

