private DistanceSensor sensorColorRange;

@Override
public void runOpMode() {
    sensorColorRange = hardwareMap.get(DistanceSensor.class, "sensorColorRange");
    // Now you can use sensorColorRange in your code
}

double distance = sensorColorRange.getDistance(DistanceUnit.CM);
if (Double.isNaN(distance)) {
    telemetry.addData("Distance", "Out of range");
} else {
    telemetry.addData("Distance (cm)", distance);
}
telemetry.update();

if (sensorColorRange.getDistance(DistanceUnit.CM) < 10) {
    // Stop motors
    leftMotor.setPower(0);
    rightMotor.setPower(0);
    telemetry.addData("Status", "Obstacle detected!");
}
telemetry.update();
