// Initialize and configure the IMU
BNO055IMU imu = hardwareMap.get(BNO055IMU.class, "imu");
BNO055IMU.Parameters parameters = new BNO055IMU.Parameters();
parameters.angleUnit = BNO055IMU.AngleUnit.DEGREES;
parameters.accelUnit = BNO055IMU.AccelUnit.METERS_PERSEC_PERSEC;
imu.initialize(parameters);
// Read orientation
Orientation angles = imu.getAngularOrientation();
telemetry.addData("Heading", angles.firstAngle);
telemetry.addData("Pitch", angles.secondAngle);
telemetry.addData("Roll", angles.thirdAngle);
telemetry.update();

// Safety check using IMU
Orientation angles = imu.getAngularOrientation();
if (Math.abs(angles.firstAngle) > 45) {
    // Robot is tilted too far
    telemetry.addData("Safety", "Robot tilt detected!");
    telemetry.update();
}

// Practice Exercise: IMU
// Create an OpMode that displays IMU data
// Include calibration and troubleshooting steps
// Test IMU response to different conditions

@TeleOp(name="IMU Test", group="Testing")
public class IMUTestOpMode extends LinearOpMode {
    private BNO055IMU imu;
    @Override
    public void runOpMode() {
        imu = hardwareMap.get(BNO055IMU.class, "imu");
        BNO055IMU.Parameters parameters = new BNO055IMU.Parameters();
        parameters.angleUnit = BNO055IMU.AngleUnit.DEGREES;
        parameters.accelUnit = BNO055IMU.AccelUnit.METERS_PERSEC_PERSEC;
        imu.initialize(parameters);
        waitForStart();
        while (opModeIsActive()) {
            Orientation angles = imu.getAngularOrientation();
            telemetry.addData("Heading", angles.firstAngle);
            telemetry.addData("Pitch", angles.secondAngle);
            telemetry.addData("Roll", angles.thirdAngle);
            telemetry.update();
        }
    }
