// Standard servo configuration and control
Servo standardServo = hardwareMap.get(Servo.class, "standard_servo");

// Standard servo position constants
final double POSITION_MIN = 0.0;      // Full counterclockwise
final double POSITION_CENTER = 0.5;   // Center position
final double POSITION_MAX = 1.0;      // Full clockwise

// Configure standard servo
standardServo.setDirection(Servo.Direction.FORWARD);
standardServo.setPosition(POSITION_CENTER); // Start at center

// Position control methods
public void setStandardServoPosition(Servo servo, double position) {
    // Ensure position is within valid range
    position = Math.max(0.0, Math.min(1.0, position));
    servo.setPosition(position);
}

// Predefined positions for common applications
public void moveToUpPosition(Servo servo) {
    setStandardServoPosition(servo, 0.0);
}

public void moveToDownPosition(Servo servo) {
    setStandardServoPosition(servo, 1.0);
}

public void moveToCenterPosition(Servo servo) {
    setStandardServoPosition(servo, 0.5);
}

// Gradual movement for smooth operation
public void moveStandardServoGradually(Servo servo, double targetPosition, double stepSize) {
    double currentPosition = servo.getPosition();

    if (currentPosition < targetPosition) {
        double newPosition = Math.min(currentPosition + stepSize, targetPosition);
        servo.setPosition(newPosition);
    } else if (currentPosition > targetPosition) {
        double newPosition = Math.max(currentPosition - stepSize, targetPosition);
        servo.setPosition(newPosition);
    }
}

// Position monitoring
public void monitorStandardServo(Servo servo, String servoName) {
    double position = servo.getPosition();
    double degrees = position * 180.0; // Convert to degrees

    telemetry.addData(servoName + " Position", "%.3f", position);
    telemetry.addData(servoName + " Degrees", "%.1f°", degrees);
}

// Continuous rotation servo control
Servo continuousServo = hardwareMap.get(Servo.class, "continuous_servo");

// Continuous servo control constants
final double CONTINUOUS_STOP = 0.5;           // Stop position
final double CONTINUOUS_FULL_FORWARD = 1.0;   // Full speed clockwise
final double CONTINUOUS_FULL_REVERSE = 0.0;   // Full speed counterclockwise
final double CONTINUOUS_HALF_FORWARD = 0.75;  // Half speed clockwise
final double CONTINUOUS_HALF_REVERSE = 0.25;  // Half speed counterclockwise

// Configure continuous servo
continuousServo.setDirection(Servo.Direction.FORWARD);
continuousServo.setPosition(CONTINUOUS_STOP); // Start stopped

// Speed control methods
public void setContinuousServoSpeed(Servo servo, double speed) {
    // speed ranges from -1.0 (full reverse) to 1.0 (full forward)
    double position = CONTINUOUS_STOP + (speed * 0.5);
    position = Math.max(0.0, Math.min(1.0, position));
    servo.setPosition(position);
}

public void stopContinuousServo(Servo servo) {
    servo.setPosition(CONTINUOUS_STOP);
}

public void setContinuousServoForward(Servo servo, double speed) {
    // speed ranges from 0.0 to 1.0
    double position = CONTINUOUS_STOP + (speed * 0.5);
    servo.setPosition(position);
}

public void setContinuousServoReverse(Servo servo, double speed) {
    // speed ranges from 0.0 to 1.0
    double position = CONTINUOUS_STOP - (speed * 0.5);
    servo.setPosition(position);
}

// Speed monitoring
public void monitorContinuousServo(Servo servo, String servoName) {
    double position = servo.getPosition();
    double speed = (position - CONTINUOUS_STOP) * 2.0; // Convert to -1.0 to 1.0 range

    telemetry.addData(servoName + " Position", "%.3f", position);
    telemetry.addData(servoName + " Speed", "%.2f", speed);

    if (Math.abs(speed) < 0.1) {
        telemetry.addData(servoName + " Status", "Stopped");
    } else if (speed > 0) {
        telemetry.addData(servoName + " Status", "Forward");
    } else {
        telemetry.addData(servoName + " Status", "Reverse");
    }
}

// Servo selection helper
public class ServoSelector {
    public enum ServoType {
        STANDARD,
        CONTINUOUS
    }

    public static ServoType recommendServoType(String application) {
        switch (application.toLowerCase()) {
            case "arm":
            case "claw":
            case "flap":
            case "lift":
            case "positioning":
                return ServoType.STANDARD;

            case "conveyor":
            case "intake":
            case "continuous":
            case "transport":
                return ServoType.CONTINUOUS;

            default:
                return ServoType.STANDARD; // Default to standard
        }
    }

    public static String getServoDescription(ServoType type) {
        switch (type) {
            case STANDARD:
                return "Position control, limited rotation, holds position";
            case CONTINUOUS:
                return "Speed control, full rotation, continuous movement";
            default:
                return "Unknown type";
        }
    }
}

// Usage examples
ServoSelector.ServoType armType = ServoSelector.recommendServoType("arm");
ServoSelector.ServoType conveyorType = ServoSelector.recommendServoType("conveyor");

// Display recommendations
telemetry.addData("Arm Servo Type", armType.toString());
telemetry.addData("Arm Description", ServoSelector.getServoDescription(armType));
telemetry.addData("Conveyor Servo Type", conveyorType.toString());
telemetry.addData("Conveyor Description", ServoSelector.getServoDescription(conveyorType));

// Control method comparison
public class ServoController {
    private Servo servo;
    private ServoSelector.ServoType type;

    public ServoController(Servo servo, ServoSelector.ServoType type) {
        this.servo = servo;
        this.type = type;

        // Initialize based on type
        if (type == ServoSelector.ServoType.STANDARD) {
            servo.setPosition(0.5); // Center position
        } else {
            servo.setPosition(0.5); // Stop position
        }
    }

    public void setValue(double value) {
        switch (type) {
            case STANDARD:
                // For standard servos, value is position (0.0-1.0)
                value = Math.max(0.0, Math.min(1.0, value));
                servo.setPosition(value);
                break;

            case CONTINUOUS:
                // For continuous servos, value is speed (-1.0 to 1.0)
                double position = 0.5 + (value * 0.5);
                position = Math.max(0.0, Math.min(1.0, position));
                servo.setPosition(position);
                break;
        }
    }

    public double getValue() {
        switch (type) {
            case STANDARD:
                // Return current position
                return servo.getPosition();

            case CONTINUOUS:
                // Return current speed
                return (servo.getPosition() - 0.5) * 2.0;

            default:
                return 0.0;
        }
    }

    public String getStatus() {
        switch (type) {
            case STANDARD:
                double position = servo.getPosition();
                return String.format("Position: %.3f (%.1f°)", position, position * 180.0);

            case CONTINUOUS:
                double speed = getValue();
                if (Math.abs(speed) < 0.1) {
                    return "Stopped";
                } else if (speed > 0) {
                    return String.format("Forward: %.2f", speed);
                } else {
                    return String.format("Reverse: %.2f", Math.abs(speed));
                }

            default:
                return "Unknown";
        }
    }
}

// Usage example
ServoController armController = new ServoController(armServo, ServoSelector.ServoType.STANDARD);
ServoController conveyorController = new ServoController(conveyorServo, ServoSelector.ServoType.CONTINUOUS);

// Control arm position
armController.setValue(0.8); // Move to 80% position

// Control conveyor speed
conveyorController.setValue(0.5); // Half speed forward

// Display status
telemetry.addData("Arm Status", armController.getStatus());
telemetry.addData("Conveyor Status", conveyorController.getStatus());

// Arm mechanism with standard servo
public class ArmMechanism {
    private ServoController armServo;
    private final double ARM_UP = 0.0;
    private final double ARM_DOWN = 1.0;
    private final double ARM_PICKUP = 0.2;
    private final double ARM_DELIVERY = 0.8;

    public ArmMechanism(Servo armServo) {
        this.armServo = new ServoController(armServo, ServoSelector.ServoType.STANDARD);
    }

    public void moveUp() {
        armServo.setValue(ARM_UP);
    }

    public void moveDown() {
        armServo.setValue(ARM_DOWN);
    }

    public void moveToPickup() {
        armServo.setValue(ARM_PICKUP);
    }

    public void moveToDelivery() {
        armServo.setValue(ARM_DELIVERY);
    }

    public boolean isAtPosition(double targetPosition, double tolerance) {
        return Math.abs(armServo.getValue() - targetPosition) <= tolerance;
    }
}

// Conveyor mechanism with continuous servo
public class ConveyorMechanism {
    private ServoController conveyorServo;
    private boolean isRunning = false;

    public ConveyorMechanism(Servo conveyorServo) {
        this.conveyorServo = new ServoController(conveyorServo, ServoSelector.ServoType.CONTINUOUS);
    }

    public void start(double speed) {
        conveyorServo.setValue(speed);
        isRunning = true;
    }

    public void stop() {
        conveyorServo.setValue(0.0);
        isRunning = false;
    }

    public void reverse(double speed) {
        conveyorServo.setValue(-speed);
        isRunning = true;
    }

    public boolean isRunning() {
        return isRunning;
    }
}

// Complete mechanism example
public class RobotMechanisms {
    private ArmMechanism arm;
    private ConveyorMechanism conveyor;

    public RobotMechanisms(Servo armServo, Servo conveyorServo) {
        this.arm = new ArmMechanism(armServo);
        this.conveyor = new ConveyorMechanism(conveyorServo);
    }

    public void pickupSequence() {
        // Move arm to pickup position
        arm.moveToPickup();

        // Start conveyor to move items
        conveyor.start(0.5);

        // Wait for items to be picked up
        sleep(2000);

        // Stop conveyor
        conveyor.stop();

        // Move arm to delivery position
        arm.moveToDelivery();
    }

    public void displayStatus() {
        telemetry.addData("Arm Status", arm.getStatus());
        telemetry.addData("Conveyor Status", conveyor.getStatus());
        telemetry.addData("Conveyor Running", conveyor.isRunning());
    }
}

// Servo troubleshooting and diagnostics
public class ServoTroubleshooter {
    private Servo servo;
    private ServoSelector.ServoType type;

    public ServoTroubleshooter(Servo servo, ServoSelector.ServoType type) {
        this.servo = servo;
        this.type = type;
    }

    public void runDiagnostics() {
        telemetry.addData("Servo Type", type.toString());
        telemetry.addData("Current Position", "%.3f", servo.getPosition());
        telemetry.addData("Direction", servo.getDirection().toString());
        telemetry.addData("Port Number", servo.getPortNumber());

        switch (type) {
            case STANDARD:
                runStandardServoTest();
                break;
            case CONTINUOUS:
                runContinuousServoTest();
                break;
        }
    }

    private void runStandardServoTest() {
        telemetry.addData("Test", "Standard Servo Position Test");

        if (gamepad1.a) {
            servo.setPosition(0.0);
            telemetry.addData("Action", "Move to position 0.0");
        } else if (gamepad1.b) {
            servo.setPosition(0.5);
            telemetry.addData("Action", "Move to position 0.5");
        } else if (gamepad1.x) {
            servo.setPosition(1.0);
            telemetry.addData("Action", "Move to position 1.0");
        }
    }

    private void runContinuousServoTest() {
        telemetry.addData("Test", "Continuous Servo Speed Test");

        if (gamepad1.a) {
            servo.setPosition(0.0); // Full reverse
            telemetry.addData("Action", "Full reverse");
        } else if (gamepad1.b) {
            servo.setPosition(0.5); // Stop
            telemetry.addData("Action", "Stop");
        } else if (gamepad1.x) {
            servo.setPosition(1.0); // Full forward
            telemetry.addData("Action", "Full forward");
        }

        // Speed control with joystick
        double speed = -gamepad1.right_stick_y;
        if (Math.abs(speed) > 0.1) {
            double position = 0.5 + (speed * 0.5);
            servo.setPosition(position);
            telemetry.addData("Speed Control", "%.2f", speed);
        }
    }
}

// Usage in OpMode
ServoTroubleshooter armTroubleshooter = new ServoTroubleshooter(armServo, ServoSelector.ServoType.STANDARD);
ServoTroubleshooter conveyorTroubleshooter = new ServoTroubleshooter(conveyorServo, ServoSelector.ServoType.CONTINUOUS);

while (opModeIsActive()) {
    armTroubleshooter.runDiagnostics();
    conveyorTroubleshooter.runDiagnostics();
    telemetry.update();
}

// Practice Exercise: Servo Type Comparison
// Create an OpMode that demonstrates both servo types
// Compare control methods and behavior
// Test different applications for each type

@TeleOp(name="Servo Type Comparison", group="Testing")
public class ServoTypeComparisonOpMode extends LinearOpMode {

    private ServoController standardController;
    private ServoController continuousController;

    @Override
    public void runOpMode() {
        // Initialize both servo types
        Servo standardServo = hardwareMap.get(Servo.class, "standard_servo");
        Servo continuousServo = hardwareMap.get(Servo.class, "continuous_servo");

        standardController = new ServoController(standardServo, ServoSelector.ServoType.STANDARD);
        continuousController = new ServoController(continuousServo, ServoSelector.ServoType.CONTINUOUS);

        waitForStart();

        while (opModeIsActive()) {
            // Control standard servo with left joystick
            double standardInput = -gamepad1.left_stick_y;
            if (Math.abs(standardInput) > 0.1) {
                // Convert joystick input to position (0.0-1.0)
                double position = (standardInput + 1.0) / 2.0;
                standardController.setValue(position);
            }

            // Control continuous servo with right joystick
            double continuousInput = -gamepad1.right_stick_y;
            if (Math.abs(continuousInput) > 0.1) {
                // Use joystick input directly as speed (-1.0 to 1.0)
                continuousController.setValue(continuousInput);
            } else {
                continuousController.setValue(0.0); // Stop
            }

            // Display status
            telemetry.addData("Standard Servo", standardController.getStatus());
            telemetry.addData("Continuous Servo", continuousController.getStatus());
            telemetry.addData("Standard Input", "%.2f", standardInput);
            telemetry.addData("Continuous Input", "%.2f", continuousInput);
            telemetry.update();
        }
    }
}
