// Declare hardware components
private DcMotor leftDrive, rightDrive;
private Servo clawServo;

// Initialize in runOpMode()
leftDrive = hardwareMap.get(DcMotor.class, "left_drive");
rightDrive = hardwareMap.get(DcMotor.class, "right_drive");
clawServo = hardwareMap.get(Servo.class, "claw_servo");

// Show initialization status
telemetry.addData("Status", "Custom TeleOp Controls Ready");
telemetry.update();

// Read joystick values for drive control
double drivePower = -gamepad1.left_stick_y;  // Forward/backward
double turnPower = gamepad1.right_stick_x;   // Left/right turning

// Read button values for claw control
boolean aPressed = gamepad1.a;  // Open claw
boolean bPressed = gamepad1.b;  // Close claw

// Display input values
telemetry.addData("Drive Power", "%.2f", drivePower);
telemetry.addData("Turn Power", "%.2f", turnPower);
telemetry.addData("Button A", aPressed);
telemetry.addData("Button B", bPressed);
telemetry.update();

// Calculate left and right motor powers
// Drive power controls forward/backward
// Turn power controls left/right turning
double leftPower = drivePower + turnPower;
double rightPower = drivePower - turnPower;

// Apply power limits to prevent motor damage
leftPower = Math.max(-1.0, Math.min(1.0, leftPower));
rightPower = Math.max(-1.0, Math.min(1.0, rightPower));

// Set motor powers
leftDrive.setPower(leftPower);
rightDrive.setPower(rightPower);

// Control claw servo based on button presses
if (aPressed) {
    clawServo.setPosition(1.0);  // Open claw
} else if (bPressed) {
    clawServo.setPosition(0.0);  // Close claw
}

// Display all control values and robot status
telemetry.addData("=== DRIVE CONTROLS ===", "");
telemetry.addData("Drive Power", "%.2f", drivePower);
telemetry.addData("Turn Power", "%.2f", turnPower);
telemetry.addData("Left Motor Power", "%.2f", leftPower);
telemetry.addData("Right Motor Power", "%.2f", rightPower);

telemetry.addData("=== CLAW CONTROLS ===", "");
telemetry.addData("Button A (Open)", aPressed);
telemetry.addData("Button B (Close)", bPressed);
telemetry.addData("Claw Position", "%.2f", clawServo.getPosition());

telemetry.addData("=== ROBOT STATUS ===", "");
telemetry.addData("OpMode Active", opModeIsActive());
telemetry.addData("Match Time", "%.1f seconds", getRuntime());

telemetry.update();

// Helper method for drive control
private void controlDrive() {
    double drivePower = -gamepad1.left_stick_y;
    double turnPower = gamepad1.right_stick_x;

    double leftPower = drivePower + turnPower;
    double rightPower = drivePower - turnPower;

    // Apply power limits
    leftPower = Math.max(-1.0, Math.min(1.0, leftPower));
    rightPower = Math.max(-1.0, Math.min(1.0, rightPower));

    leftDrive.setPower(leftPower);
    rightDrive.setPower(rightPower);
}

// Helper method for claw control
private void controlClaw() {
    if (gamepad1.a) {
        clawServo.setPosition(1.0);  // Open
    } else if (gamepad1.b) {
        clawServo.setPosition(0.0);  // Close
    }
}

// Helper method for telemetry
private void updateTelemetry() {
    telemetry.addData("Drive Power", "%.2f", -gamepad1.left_stick_y);
telemetry.addData("Turn Power", "%.2f", gamepad1.right_stick_x);
telemetry.addData("Left Motor", "%.2f", leftDrive.getPower());
telemetry.addData("Right Motor", "%.2f", rightDrive.getPower());
telemetry.addData("Claw Position", "%.2f", clawServo.getPosition());
telemetry.addData("Button A", gamepad1.a);
telemetry.addData("Button B", gamepad1.b);
telemetry.update();
}

import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.eventloop.opmode.TeleOp;
import com.qualcomm.robotcore.hardware.DcMotor;
import com.qualcomm.robotcore.hardware.Servo;

@TeleOp(name = "CustomTeleOpControls")
public class CustomTeleOpControls extends LinearOpMode {
    private DcMotor leftDrive, rightDrive;
    private Servo clawServo;

    @Override
    public void runOpMode() {
        // Initialize hardware
        leftDrive = hardwareMap.get(DcMotor.class, "left_drive");
        rightDrive = hardwareMap.get(DcMotor.class, "right_drive");
        clawServo = hardwareMap.get(Servo.class, "claw_servo");

        telemetry.addData("Status", "Custom TeleOp Controls Ready");
telemetry.addData("Instructions", "Left stick: Drive, Right stick: Turn, A/B: Claw");
telemetry.update();

        waitForStart();

        while (opModeIsActive()) {
            // Control drive system
            controlDrive();

            // Control claw
            controlClaw();

            // Update telemetry
            updateTelemetry();
        }

        // Stop motors when OpMode ends
        leftDrive.setPower(0);
        rightDrive.setPower(0);
    }

    // Helper method for drive control
    private void controlDrive() {
        double drivePower = -gamepad1.left_stick_y;
        double turnPower = gamepad1.right_stick_x;

        double leftPower = drivePower + turnPower;
        double rightPower = drivePower - turnPower;

        // Apply power limits
        leftPower = Math.max(-1.0, Math.min(1.0, leftPower));
        rightPower = Math.max(-1.0, Math.min(1.0, rightPower));

        leftDrive.setPower(leftPower);
        rightDrive.setPower(rightPower);
    }

    // Helper method for claw control
    private void controlClaw() {
        if (gamepad1.a) {
            clawServo.setPosition(1.0);  // Open
        } else if (gamepad1.b) {
            clawServo.setPosition(0.0);  // Close
        }
    }

    // Helper method for telemetry
    private void updateTelemetry() {
        telemetry.addData("=== DRIVE CONTROLS ===", "");
telemetry.addData("Drive Power", "%.2f", -gamepad1.left_stick_y);
telemetry.addData("Turn Power", "%.2f", gamepad1.right_stick_x);
telemetry.addData("Left Motor", "%.2f", leftDrive.getPower());
telemetry.addData("Right Motor", "%.2f", rightDrive.getPower());

telemetry.addData("=== CLAW CONTROLS ===", "");
telemetry.addData("Button A (Open)", gamepad1.a);
telemetry.addData("Button B (Close)", gamepad1.b);
telemetry.addData("Claw Position", "%.2f", clawServo.getPosition());

telemetry.addData("=== ROBOT STATUS ===", "");
telemetry.addData("Match Time", "%.1f seconds", getRuntime());
telemetry.update();
    }
}
