import java.util.Scanner;

public class NumberValidator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int validNumbers = 0;
        int invalidAttempts = 0;
        double sum = 0.0;
        final int TARGET_NUMBERS = 5;

        System.out.println("=== Robot Sensor Value Validator ===");
        System.out.println("Enter " + TARGET_NUMBERS + " valid sensor readings (0.0 - 100.0)");
        System.out.println("Type 'quit' to exit early\n");

        while (validNumbers < TARGET_NUMBERS) {
            System.out.print("Sensor reading " + (validNumbers + 1) + "/" + TARGET_NUMBERS + ": ");
            String input = scanner.nextLine().trim();

            // Check for quit command
            if (input.equalsIgnoreCase("quit")) {
                System.out.println("Validation stopped by user.");
                break;
            }

            // Try to parse the number
            double reading;
            try {
                reading = Double.parseDouble(input);
            } catch (NumberFormatException e) {
                System.out.println("‚ùå Invalid format! Please enter a decimal number.");
                invalidAttempts++;
                continue;  // Skip this iteration, try again
            }

            // Validate range
            if (reading < 0.0 || reading > 100.0) {
                System.out.println("‚ùå Out of range! Value must be between 0.0 and 100.0");
                System.out.println("   Your input: " + reading);
                invalidAttempts++;
                continue;  // Skip this iteration, try again
            }

            // Check for suspicious values
            if (reading == 0.0 || reading == 100.0) {
                System.out.print("‚ö†Ô∏è Extreme value detected (" + reading + "). Are you sure? (y/n): ");
                String confirm = scanner.nextLine().trim().toLowerCase();

                if (!confirm.equals("y") && !confirm.equals("yes")) {
                    System.out.println("Value rejected by user.");
                    invalidAttempts++;
                    continue;  // Skip this iteration
                }
            }

            // Value is valid!
            validNumbers++;
            sum += reading;
            System.out.println("‚úÖ Valid reading accepted: " + reading);

            // Show progress
            if (validNumbers < TARGET_NUMBERS) {
                double currentAverage = sum / validNumbers;
                System.out.println("   Current average: " + String.format("%.2f", currentAverage));
                System.out.println("   Progress: " + validNumbers + "/" + TARGET_NUMBERS + " readings");
            }
        }

        // Final results
        System.out.println("\n=== Validation Complete ===");
        System.out.println("Valid readings collected: " + validNumbers);
        System.out.println("Invalid attempts: " + invalidAttempts);

        if (validNumbers > 0) {
            double average = sum / validNumbers;
            System.out.println("Average sensor value: " + String.format("%.2f", average));

            // Assess data quality
            double errorRate = (double) invalidAttempts / (validNumbers + invalidAttempts) * 100;
            System.out.println("Error rate: " + String.format("%.1f", errorRate) + "%");

            if (errorRate < 10) {
                System.out.println("‚úÖ Excellent data quality");
            } else if (errorRate < 25) {
                System.out.println("‚ö†Ô∏è Acceptable data quality");
            } else {
                System.out.println("‚ùå Poor data quality - check input process");
            }
        }

        System.out.println("Number validation complete.");
    }
}

import java.util.Scanner;

public class RobotControlMenu {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        boolean robotArmed = false;
        int batteryLevel = 85;
        String currentMode = "STOPPED";
        int commandCount = 0;

        System.out.println("=== FRC Robot Control System ===");
        System.out.println("Robot initialized and ready for commands\n");

        while (true) {
            // Display current status
            System.out.println("\n=== Robot Status ===");
            System.out.println("Mode: " + currentMode);
            System.out.println("Armed: " + (robotArmed ? "YES" : "NO"));
            System.out.println("Battery: " + batteryLevel + "%");
            System.out.println("Commands executed: " + commandCount);

            // Display menu
            System.out.println("\n=== Control Menu ===");
            System.out.println("1. Arm/Disarm robot");
            System.out.println("2. Start autonomous mode");
            System.out.println("3. Start teleop mode");
            System.out.println("4. Emergency stop");
            System.out.println("5. Battery status");
            System.out.println("6. System diagnostics");
            System.out.println("0. Shutdown robot");
            System.out.print("\nEnter command: ");

            String input = scanner.nextLine().trim();
            commandCount++;

            // Process menu choice
            if (input.equals("0")) {
                System.out.println("\nüî¥ SHUTDOWN SEQUENCE INITIATED");
                System.out.print("Are you sure you want to shutdown? (y/n): ");
                String confirm = scanner.nextLine().trim().toLowerCase();

                if (confirm.equals("y") || confirm.equals("yes")) {
                    System.out.println("\nü§ñ Shutting down robot systems...");
                    System.out.println("   ‚Üí Stopping all motors");
                    System.out.println("   ‚Üí Disabling sensors");
                    System.out.println("   ‚Üí Saving configuration");
                    System.out.println("   ‚Üí Robot shutdown complete");
                    break;  // Exit the main loop
                } else {
                    System.out.println("Shutdown cancelled.");
                }

            } else if (input.equals("1")) {
                robotArmed = !robotArmed;
                System.out.println("üîß Robot " + (robotArmed ? "ARMED" : "DISARMED"));

                if (robotArmed) {
                    System.out.println("   ‚ö†Ô∏è Robot is now ready for operation");
                    System.out.println("   ‚ö†Ô∏è Use caution when executing commands");
                } else {
                    currentMode = "STOPPED";
                    System.out.println("   ‚úÖ Robot is now safe");
                }

            } else if (input.equals("2")) {
                if (!robotArmed) {
                    System.out.println("‚ùå Cannot start autonomous - robot not armed");
                } else if (batteryLevel < 20) {
                    System.out.println("‚ùå Cannot start autonomous - low battery");
                } else {
                    currentMode = "AUTONOMOUS";
                    System.out.println("ü§ñ Autonomous mode started");
                    System.out.println("   ‚Üí Robot executing pre-programmed sequence");
                    batteryLevel -= 5;  // Simulate battery usage
                }

            } else if (input.equals("3")) {
                if (!robotArmed) {
                    System.out.println("‚ùå Cannot start teleop - robot not armed");
                } else {
                    currentMode = "TELEOP";
                    System.out.println("üéÆ Teleop mode started");
                    System.out.println("   ‚Üí Robot ready for manual control");
                    batteryLevel -= 2;  // Simulate battery usage
                }

            } else if (input.equals("4")) {
                currentMode = "EMERGENCY_STOP";
                robotArmed = false;
                System.out.println("üö® EMERGENCY STOP ACTIVATED");
                System.out.println("   ‚Üí All motors stopped immediately");
                System.out.println("   ‚Üí Robot automatically disarmed");
                System.out.println("   ‚Üí Check robot before re-arming");

            } else if (input.equals("5")) {
                System.out.println("üîã Battery Status Report:");
                System.out.println("   Current level: " + batteryLevel + "%");

                if (batteryLevel > 80) {
                    System.out.println("   Status: ‚úÖ Excellent");
                } else if (batteryLevel > 50) {
                    System.out.println("   Status: ‚ö†Ô∏è Good");
                } else if (batteryLevel > 20) {
                    System.out.println("   Status: ‚ö†Ô∏è Low - consider charging");
                } else {
                    System.out.println("   Status: üî¥ Critical - charge immediately");
                }

                int estimatedRuntime = batteryLevel / 5;  // Rough estimate
                System.out.println("   Estimated runtime: " + estimatedRuntime + " minutes");

            } else if (input.equals("6")) {
                System.out.println("üîç System Diagnostics:");
                System.out.println("   Motors: ‚úÖ All operational");
                System.out.println("   Sensors: ‚úÖ All responding");
                System.out.println("   Communication: ‚úÖ Strong signal");
                System.out.println("   Temperature: ‚úÖ Normal (" + (int)(Math.random() * 10 + 65) + "¬∞F)");
                System.out.println("   Voltage: ‚úÖ Stable (" + String.format("%.1f", 11.5 + Math.random()) + "V)");

            } else {
                System.out.println("‚ùå Invalid command: " + input);
                System.out.println("   Please enter a number 0-6");
                commandCount--;  // Don't count invalid commands
            }

            // Simulate battery drain over time
            if (commandCount % 3 == 0 && batteryLevel > 0) {
                batteryLevel = Math.max(0, batteryLevel - 1);
            }

            // Critical battery warning
            if (batteryLevel <= 15 && robotArmed) {
                System.out.println("\n‚ö†Ô∏è WARNING: Critical battery level!");
                System.out.println("   Consider shutdown to prevent damage");
            }

            // Auto-shutdown on dead battery
            if (batteryLevel <= 0) {
                System.out.println("\nüî¥ CRITICAL: Battery depleted!");
                System.out.println("ü§ñ Initiating emergency shutdown...");
                break;  // Emergency exit
            }
        }

        // Cleanup and final status
        System.out.println("\n=== Final Status Report ===");
        System.out.println("Final mode: " + currentMode);
        System.out.println("Final battery: " + batteryLevel + "%");
        System.out.println("Commands processed: " + commandCount);
        System.out.println("\nRobot control system terminated.");
        System.out.println("Thank you for using FRC Robot Control!");
    }
}
