// Complete example:
public class RobotInfo {
    public static void main(String[] args) {
        String teamName = "Lightning Bolts";
        int numberOfWheels = 4;
        double weightInPounds = 125.5;
        boolean isAutonomous = true;

        System.out.println("=== Robot Information ===");
        System.out.println("Team: " + teamName);
        System.out.println("Wheels: " + numberOfWheels);
        System.out.println("Weight: " + weightInPounds + " lbs");
        System.out.println("Autonomous: " + isAutonomous);
    }
}
