public class CountingLoop {
    public static void main(String[] args) {
        // Method 1: Using while with condition
        System.out.println("=== Counting 1 to 10 (Conditional While) ===");
        int count = 1;

        while (count <= 10) {
            System.out.println("Count: " + count);
            count++;
        }

        System.out.println("Counting complete!");

        // Method 2: Using while(true) with break
        System.out.println("\n=== Counting 1 to 10 (Infinite While + Break) ===");
        int num = 1;

        while (true) {
            System.out.println("Number: " + num);

            if (num >= 10) {
                break;
            }

            num++;
        }

        System.out.println("Break counting complete!");

        // Robot example: Motor speed ramp
        System.out.println("\n=== Robot Motor Speed Ramp ===");
        int motorSpeed = 0;

        while (motorSpeed < 100) {
            System.out.println("Motor speed: " + motorSpeed + "%");
            motorSpeed += 10;  // Increase by 10% each step

            // Simulate delay
            try {
                Thread.sleep(200);
            } catch (InterruptedException e) {
                break;
            }
        }

        System.out.println("Motor at full speed: " + motorSpeed + "%");
    }
}

import java.util.Scanner;

public class BatteryMonitor {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        double currentVoltage = 12.6;  // Starting voltage
        int readingCount = 0;
        double totalVoltage = 0.0;
        int lowBatteryWarnings = 0;

        System.out.println("=== Robot Battery Monitor ===");
        System.out.println("Commands: 'reading' to simulate reading, 'exit' to quit");
        System.out.println("Current battery: " + currentVoltage + "V");

        while (true) {
            System.out.print("\nBattery Monitor> ");
            String command = scanner.nextLine().trim().toLowerCase();

            // Exit condition
            if (command.equals("exit") || command.equals("quit")) {
                System.out.println("Battery monitoring stopped.");
                break;
            }

            // Simulate battery reading
            if (command.equals("reading")) {
                // Simulate voltage drop over time
                currentVoltage -= (Math.random() * 0.1 + 0.05);
                readingCount++;
                totalVoltage += currentVoltage;

                System.out.println("üìä Battery Reading #" + readingCount);
                System.out.println("Voltage: " + String.format("%.2f", currentVoltage) + "V");

                // Battery status analysis
                if (currentVoltage >= 12.0) {
                    System.out.println("üü¢ Battery Status: GOOD");
                } else if (currentVoltage >= 11.5) {
                    System.out.println("üü° Battery Status: MODERATE");
                } else if (currentVoltage >= 11.0) {
                    System.out.println("üü† Battery Status: LOW");
                    lowBatteryWarnings++;
                } else {
                    System.out.println("üî¥ Battery Status: CRITICAL - STOP ROBOT!");
                    lowBatteryWarnings++;
                    System.out.println("‚ö†Ô∏è Automatic shutdown triggered!");
                    break;  // Critical battery - emergency stop
                }

                // Show average
                if (readingCount > 0) {
                    double average = totalVoltage / readingCount;
                    System.out.println("Average voltage: " + String.format("%.2f", average) + "V");
                }

            } else if (command.equals("status")) {
                System.out.println("\n=== Battery Status Report ===");
                System.out.println("Current voltage: " + String.format("%.2f", currentVoltage) + "V");
                System.out.println("Readings taken: " + readingCount);
                System.out.println("Low battery warnings: " + lowBatteryWarnings);

            } else if (command.equals("help")) {
                System.out.println("\nAvailable commands:");
                System.out.println("  reading - Take a battery voltage reading");
                System.out.println("  status  - Show battery status report");
                System.out.println("  exit    - Exit battery monitor");
                System.out.println("  help    - Show this help");

            } else {
                System.out.println("‚ùå Unknown command: " + command);
                System.out.println("Type 'help' for available commands.");
            }
        }

        // Final report
        System.out.println("\n=== Final Battery Report ===");
        System.out.println("Total readings: " + readingCount);
        System.out.println("Final voltage: " + String.format("%.2f", currentVoltage) + "V");
        System.out.println("Low battery warnings: " + lowBatteryWarnings);

        if (readingCount > 0) {
            double average = totalVoltage / readingCount;
            System.out.println("Average voltage: " + String.format("%.2f", average) + "V");
        }

        System.out.println("Battery monitor shutdown complete.");
    }
}

import java.util.Scanner;
import java.util.Random;

public class NumberGuessingGame {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        Random random = new Random();

        // Game setup
        int targetNumber = random.nextInt(100) + 1;  // 1-100
        int attempts = 0;
        int maxAttempts = 7;
        boolean gameWon = false;

        System.out.println("=== Robot Sensor Calibration Game ===");
        System.out.println("The robot sensor needs calibration!");
        System.out.println("Guess the correct sensor value (1-100)");
        System.out.println("You have " + maxAttempts + " attempts.");

        while (true) {
            System.out.print("\nAttempt " + (attempts + 1) + " of " + maxAttempts + ": ");
            String input = scanner.nextLine().trim();

            // Check for quit command
            if (input.equalsIgnoreCase("quit") || input.equalsIgnoreCase("exit")) {
                System.out.println("Game ended. The sensor value was: " + targetNumber);
                break;
            }

            // Validate input
            int guess;
            try {
                guess = Integer.parseInt(input);
            } catch (NumberFormatException e) {
                System.out.println("‚ùå Invalid input! Please enter a number (1-100) or 'quit'");
                continue;  // Don't count invalid inputs as attempts
            }

            // Check range
            if (guess < 1 || guess > 100) {
                System.out.println("‚ùå Out of range! Enter a number between 1 and 100.");
                continue;  // Don't count out-of-range as attempts
            }

            // Count valid attempt
            attempts++;

            // Check guess
            if (guess == targetNumber) {
                gameWon = true;
                System.out.println("üéâ CORRECT! Sensor calibrated successfully!");
                System.out.println("You found the value " + targetNumber + " in " + attempts + " attempts.");
                break;

            } else if (guess < targetNumber) {
                int difference = targetNumber - guess;
                System.out.println("üìà Too low! Sensor needs higher calibration.");

                if (difference <= 5) {
                    System.out.println("üî• Very close! You're within 5 points.");
                } else if (difference <= 15) {
                    System.out.println("‚ö° Getting warmer!");
                }

            } else {
                int difference = guess - targetNumber;
                System.out.println("üìâ Too high! Sensor needs lower calibration.");

                if (difference <= 5) {
                    System.out.println("üî• Very close! You're within 5 points.");
                } else if (difference <= 15) {
                    System.out.println("‚ö° Getting warmer!");
                }
            }

            // Check if attempts exhausted
            if (attempts >= maxAttempts) {
                System.out.println("\nüí• Game Over! No more attempts remaining.");
                System.out.println("The correct sensor value was: " + targetNumber);
                break;
            }

            // Show remaining attempts
            int remaining = maxAttempts - attempts;
            System.out.println("Attempts remaining: " + remaining);
        }

        // Game summary
        System.out.println("\n=== Game Summary ===");
        System.out.println("Target value: " + targetNumber);
        System.out.println("Attempts used: " + attempts);

        if (gameWon) {
            if (attempts == 1) {
                System.out.println("üèÜ PERFECT! First try calibration!");
            } else if (attempts <= 3) {
                System.out.println("‚≠ê EXCELLENT! Quick calibration!");
            } else if (attempts <= 5) {
                System.out.println("‚úÖ GOOD! Efficient calibration.");
            } else {
                System.out.println("‚úÖ SUCCESS! Sensor calibrated.");
            }
        } else {
            System.out.println("‚ùå Calibration failed. Try again!");
        }

        System.out.println("Calibration session complete.");
    }
}

import java.util.Random;

public class SensorTargetLoop {
    public static void main(String[] args) {
        Random random = new Random();

        // Target sensor configuration
        final double TARGET_VALUE = 75.0;
        final double TOLERANCE = 2.0;  // ¬±2.0 tolerance
        final int MAX_READINGS = 20;

        // Sensor simulation variables
        double currentReading = 50.0;  // Starting point
        int readingCount = 0;
        boolean targetReached = false;

        System.out.println("=== Robot Sensor Target Detection ===");
        System.out.println("Target value: " + TARGET_VALUE);
        System.out.println("Tolerance: ¬±" + TOLERANCE);
        System.out.println("Acceptable range: " + (TARGET_VALUE - TOLERANCE) + " - " + (TARGET_VALUE + TOLERANCE));
        System.out.println("Maximum readings: " + MAX_READINGS);
        System.out.println("\nStarting sensor readings...");

        // Main sensor reading loop
        while (!targetReached && readingCount < MAX_READINGS) {
            readingCount++;

            // Simulate sensor reading with gradual approach to target
            double adjustment = (TARGET_VALUE - currentReading) * 0.3;  // 30% correction
            double noise = (random.nextDouble() - 0.5) * 4.0;  // ¬±2.0 noise
            currentReading += adjustment + noise;

            // Ensure reading stays in realistic range
            if (currentReading < 0) currentReading = 0;
            if (currentReading > 100) currentReading = 100;

            System.out.println("Reading #" + String.format("%2d", readingCount) + 
                             ": " + String.format("%6.2f", currentReading));

            // Check if target is reached
            double difference = Math.abs(currentReading - TARGET_VALUE);

            if (difference <= TOLERANCE) {
                targetReached = true;
                System.out.println("üéØ TARGET REACHED!");
                System.out.println("   Target: " + TARGET_VALUE);
                System.out.println("   Actual: " + String.format("%.2f", currentReading));
                System.out.println("   Difference: " + String.format("%.2f", difference));
            } else {
                // Provide guidance
                if (difference <= 5.0) {
                    System.out.println("   üî• Very close! (" + String.format("%.2f", difference) + " away)");
                } else if (difference <= 10.0) {
                    System.out.println("   ‚ö° Getting closer (" + String.format("%.2f", difference) + " away)");
                } else {
                    if (currentReading < TARGET_VALUE) {
                        System.out.println("   üìà Need higher value");
                    } else {
                        System.out.println("   üìâ Need lower value");
                    }
                }
            }

            // Simulate processing delay
            try {
                Thread.sleep(300);  // 300ms delay
            } catch (InterruptedException e) {
                System.out.println("Reading interrupted!");
                break;
            }
        }

        // Final results
        System.out.println("\n=== Sensor Detection Results ===");
        System.out.println("Total readings taken: " + readingCount);
        System.out.println("Final sensor value: " + String.format("%.2f", currentReading));
        System.out.println("Target value: " + TARGET_VALUE);
        System.out.println("Final difference: " + String.format("%.2f", Math.abs(currentReading - TARGET_VALUE)));

        if (targetReached) {
            System.out.println("\n‚úÖ SUCCESS: Target reached in " + readingCount + " readings!");

            if (readingCount <= 5) {
                System.out.println("üèÜ EXCELLENT: Very fast convergence!");
            } else if (readingCount <= 10) {
                System.out.println("‚≠ê GOOD: Quick convergence");
            } else {
                System.out.println("‚úÖ ACCEPTABLE: Target achieved");
            }
        } else {
            System.out.println("\n‚ùå FAILED: Target not reached within " + MAX_READINGS + " readings");
            System.out.println("Consider adjusting sensor parameters or increasing reading limit.");
        }

        System.out.println("Sensor detection complete.");
    }
}
